  1002→       ON CONFLICT DO NOTHING`,
  1003→      [stripeCustomerId, charged]
  1004→    );
  1005→
  1006→    await client.query('COMMIT');
  1007→
  1008→    const newCredits = await getMusicCredits(stripeCustomerId);
  1009→    return {
  1010→      success: true,
  1011→      remaining: newCredits.remaining,
  1012→      charged
  1013→    };
  1014→
  1015→  } catch (err) {
