"use client";

import { useState } from "react";
import Link from "next/link";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Header } from "@/components/landing/Header";
import { Footer } from "@/components/landing/Footer";
import { motion } from "framer-motion";

export default function LoginPage() {
    const [email, setEmail] = useState("");
    const [licenseKey, setLicenseKey] = useState("");
    const [isLoading, setIsLoading] = useState(false);

    const handleLogin = async (e: React.FormEvent) => {
        e.preventDefault();
        setIsLoading(true);
        // Mock login delay
        await new Promise((resolve) => setTimeout(resolve, 1500));
        setIsLoading(false);
        alert("This is a demo login page.");
    };

    return (
        <div className="min-h-screen bg-black text-white flex flex-col font-sans">
            <Header />

            <main className="flex-grow flex items-center justify-center pt-24 pb-12 px-4 relative overflow-hidden">
                {/* Background Gradients */}
                <div className="absolute top-0 left-1/4 w-[500px] h-[500px] bg-primary/20 rounded-full blur-[120px] pointer-events-none" />
                <div className="absolute bottom-0 right-1/4 w-[500px] h-[500px] bg-purple-500/10 rounded-full blur-[120px] pointer-events-none" />

                <motion.div
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    className="w-full max-w-md space-y-8 relative z-10"
                >
                    <div className="text-center space-y-2">
                        <h1 className="text-3xl font-bold tracking-tight">Welcome Back</h1>
                        <p className="text-muted-foreground">Enter your license key to manage your subscription.</p>
                    </div>

                    <div className="glass p-8 rounded-2xl border border-white/10 shadow-2xl backdrop-blur-xl">
                        <form onSubmit={handleLogin} className="space-y-6">
                            <div className="space-y-2">
                                <label htmlFor="email" className="text-sm font-medium text-gray-300">Email</label>
                                <Input
                                    id="email"
                                    type="email"
                                    placeholder="you@example.com"
                                    value={email}
                                    onChange={(e) => setEmail(e.target.value)}
                                    className="bg-black/40 border-white/10 text-white placeholder:text-gray-500 focus:border-primary/50"
                                    required
                                />
                            </div>

                            <div className="space-y-2">
                                <label htmlFor="license" className="text-sm font-medium text-gray-300">License Key</label>
                                <Input
                                    id="license"
                                    type="text"
                                    placeholder="SPLICE-XXXX-XXXX"
                                    value={licenseKey}
                                    onChange={(e) => setLicenseKey(e.target.value)}
                                    className="bg-black/40 border-white/10 text-white placeholder:text-gray-500 focus:border-primary/50"
                                    required
                                />
                            </div>

                            <Button
                                type="submit"
                                className="w-full bg-primary hover:bg-primary/90 text-black font-semibold h-11"
                                disabled={isLoading}
                            >
                                {isLoading ? "Verifying..." : "Access Dashboard"}
                            </Button>
                        </form>

                        <div className="mt-6 text-center text-sm text-gray-400">
                            <p>Don&apos;t have a license? <Link href="/#pricing" className="text-primary hover:underline">Get SPLICE</Link></p>
                        </div>
                    </div>
                </motion.div>
            </main>

            <Footer />
        </div>
    );
}

"use client";

import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { motion } from "framer-motion";
import {
  BookOpen,
  Zap,
  Settings,
  HelpCircle,
  Code,
  Layers,
  ArrowRight,
} from "lucide-react";
import Link from "next/link";
import { Header } from "@/components/landing/Header";
import { Footer } from "@/components/landing/Footer";

const docs = [
  {
    title: "Quick Start",
    description: "Get up and running with SPLICE in under 5 minutes.",
    icon: Zap,
    href: "/docs/quickstart",
  },
  {
    title: "Installation",
    description: "Detailed installation instructions for CEP and UXP versions.",
    icon: Settings,
    href: "/docs/installation",
  },
  {
    title: "Features",
    description: "Explore all features: silence removal, AI music, captions, and more.",
    icon: Layers,
    href: "/docs/features",
  },
  {
    title: "API Reference",
    description: "Complete API documentation for advanced integrations.",
    icon: Code,
    href: "/docs/api",
  },
  {
    title: "Tutorials",
    description: "Step-by-step guides for common editing workflows.",
    icon: BookOpen,
    href: "/docs/tutorials",
  },
  {
    title: "FAQ & Troubleshooting",
    description: "Common questions and solutions to known issues.",
    icon: HelpCircle,
    href: "/docs/faq",
  },
];

export default function DocsPage() {
  return (
    <div className="min-h-screen bg-[#060b14] dark">
      <Header />
      <main className="pt-24 pb-16">
        <div className="container mx-auto px-4">
          {/* Hero */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
            className="text-center mb-12"
          >
            <div className="w-16 h-16 rounded-xl bg-[#00D4FF]/10 flex items-center justify-center mx-auto mb-4">
              <BookOpen className="w-8 h-8 text-[#00D4FF]" />
            </div>
            <h1 className="text-4xl md:text-5xl font-bold mb-4 text-white">
              <span className="gradient-text">Documentation</span>
            </h1>
            <p className="text-lg text-gray-400 max-w-2xl mx-auto">
              Everything you need to master SPLICE and supercharge your editing workflow.
            </p>
          </motion.div>

          {/* Docs grid */}
          <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6 max-w-5xl mx-auto">
            {docs.map((doc, index) => (
              <motion.div
                key={doc.title}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: index * 0.1 }}
              >
                <Link href={doc.href}>
                  <Card className="h-full bg-[#0d1420] border-gray-800 hover:border-[#00D4FF]/50 transition-colors group cursor-pointer">
                    <CardHeader>
                      <div className="w-12 h-12 rounded-lg bg-[#00D4FF]/10 flex items-center justify-center mb-4 group-hover:bg-[#00D4FF]/20 transition-colors">
                        <doc.icon className="w-6 h-6 text-[#00D4FF]" />
                      </div>
                      <CardTitle className="flex items-center justify-between text-white">
                        {doc.title}
                        <ArrowRight className="w-4 h-4 opacity-0 group-hover:opacity-100 transition-opacity text-[#00D4FF]" />
                      </CardTitle>
                    </CardHeader>
                    <CardContent>
                      <p className="text-gray-400">{doc.description}</p>
                    </CardContent>
                  </Card>
                </Link>
              </motion.div>
            ))}
          </div>

          {/* Quick links */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 0.6 }}
            className="mt-16 text-center"
          >
            <h2 className="text-xl font-bold mb-4 text-white">Popular Topics</h2>
            <div className="flex flex-wrap justify-center gap-2">
              {[
                { label: "Silence Removal", href: "/docs/features#silence" },
                { label: "AI Music Generation", href: "/docs/features#music" },
                { label: "Animated Captions", href: "/docs/features#captions" },
                { label: "Text-Based Editing", href: "/docs/features#text-editing" },
                { label: "Social Reframe", href: "/docs/features#reframe" },
                { label: "Multitrack Editing", href: "/docs/tutorials#multitrack" },
                { label: "License Activation", href: "/docs/faq#license" },
                { label: "Troubleshooting", href: "/docs/faq" },
              ].map((topic) => (
                <Link
                  key={topic.label}
                  href={topic.href}
