content.content:
     1→/**
     2→ * SPLICE CEP Panel Styles
     3→ * Premium "Deep Space" Theme
     4→ */
     5→
     6→:root {
     7→    /* Palette */
     8→    --bg-core: #121212;
     9→    --bg-panel: var(--bg-panel);
    10→    --bg-card: rgba(30, 30, 30, 0.6);
    11→    --glass-border: rgba(255, 255, 255, 0.08);
    12→    --glass-highlight: rgba(255, 255, 255, 0.03);
    13→
    14→    /* Accents */
    15→    --accent-primary: var(--accent-primary);
    16→    --accent-gradient: linear-gradient(135deg, var(--accent-primary), #a64dff);
    17→    --accent-glow: 0 0 20px rgba(74, 158, 255, 0.15);
    18→
    19→    /* Functional Colors */
    20→    --text-main: #f0f0f0;
    21→    --text-muted: var(--text-muted)888;
    22→    --success: #00e676;
    23→    --error: #ff5252;
    24→
    25→    /* Spacing & Radius */
    26→    --radius-sm: 6px;
    27→    --radius-md: 12px;
    28→    --radius-lg: 16px;
    29→    --spacing-xs: 4px;
    30→    --spacing-sm: 8px;
    31→    --spacing-md: 16px;
    32→
    33→    /* Transitions */
    34→    --ease-smooth: cubic-bezier(0.4, 0.0, 0.2, 1);
    35→}
    36→
    37→/* Reset & Base */
    38→* {
    39→    box-sizing: border-box;
    40→    margin: 0;
    41→    padding: 0;
    42→}
    43→
    44→body {
    45→    font-family: 'Inter', -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif;
    46→    background: var(--bg-core);
    47→    color: var(--text-main);
    48→    margin: 0;
    49→    padding: var(--spacing-md);
    50→    height: 100vh;
    51→    overflow-y: auto;
    52→    overflow-x: hidden;
    53→    font-size: 12px;
    54→    line-height: 1.5;
    55→    -webkit-font-smoothing: antialiased;
    56→}
    57→
    58→/* Loading Overlay */
    59→#loading-overlay {
    60→    position: fixed;
    61→    top: 0;
    62→    left: 0;
    63→    width: 100%;
    64→    height: 100%;
    65→    background: var(--bg-core);
    66→    z-index: 9999;
    67→    display: flex;
    68→    flex-direction: column;
    69→    align-items: center;
    70→    justify-content: center;
    71→    transition: opacity 0.5s var(--ease-smooth), visibility 0.5s;
    72→}
    73→
    74→#loading-overlay.hidden {
    75→    opacity: 0;
    76→    visibility: hidden;
    77→    pointer-events: none;
    78→}
    79→
    80→.loader-logo {
    81→    width: 48px;
    82→    height: 48px;
    83→    border-radius: 50%;
    84→    background: var(--accent-gradient);
    85→    animation: pulse-ring 2s infinite;
    86→    margin-bottom: var(--spacing-md);
    87→    box-shadow: var(--accent-glow);
    88→}
    89→
    90→.loader-text {
    91→    font-size: 11px;
    92→    color: var(--text-muted);
    93→    letter-spacing: 1px;
    94→    text-transform: uppercase;
    95→    font-weight: 500;
    96→}
    97→
    98→@keyframes pulse-ring {
    99→    0% {
   100→        transform: scale(0.95);
   101→        box-shadow: 0 0 0 0 rgba(74, 158, 255, 0.7);
   102→    }
   103→
   104→    70% {
   105→        transform: scale(1);
   106→        box-shadow: 0 0 0 10px rgba(74, 158, 255, 0);
   107→    }
   108→
   109→    100% {
   110→        transform: scale(0.95);
   111→        box-shadow: 0 0 0 0 rgba(74, 158, 255, 0);
   112→    }
   113→}
   114→
   115→/* Scrollbar */
   116→::-webkit-scrollbar {
   117→    width: 6px;
   118→}
   119→
   120→::-webkit-scrollbar-track {
   121→    background: transparent;
   122→}
   123→
   124→::-webkit-scrollbar-thumb {
   125→    background: rgba(255, 255, 255, 0.1);
   126→    border-radius: 3px;
   127→}
   128→
   129→::-webkit-scrollbar-thumb:hover {
   130→    background: rgba(255, 255, 255, 0.2);
   131→}
   132→
   133→/* Base UI Components */
   134→button {
   135→    font-family: inherit;
   136→}
   137→
   138→/* Header */
   139→.header {
   140→    display: flex;
   141→    justify-content: space-between;
   142→    align-items: center;
   143→    margin-bottom: var(--spacing-md);
   144→    padding-bottom: var(--spacing-sm);
   145→    border-bottom: 1px solid var(--glass-border);
   146→}
   147→
   148→.header h1 {
   149→    font-size: 16px;
   150→    font-weight: 700;
   151→    margin: 0;
   152→    background: var(--accent-gradient);
   153→    -webkit-background-clip: text;
   154→    background-clip: text;
   155→    -webkit-text-fill-color: transparent;
   156→    letter-spacing: -0.5px;
   157→}
   158→
   159→.header-actions {
   160→    display: flex;
   161→    gap: var(--spacing-sm);
   162→    align-items: center;
   163→}
   164→
   165→/* Action Card */
   166→.action-card {
   167→    background: var(--bg-card);
   168→    backdrop-filter: blur(10px);
   169→    border: 1px solid var(--glass-border);
   170→    border-radius: var(--radius-md);
   171→    padding: var(--spacing-md);
   172→    margin-bottom: var(--spacing-md);
   173→    box-shadow: 0 4px 20px rgba(0, 0, 0, 0.2);
   174→    transition: transform 0.2s var(--ease-smooth);
   175→}
   176→
   177→.action-card h2 {
   178→    font-size: 10px;
   179→    font-weight: 700;
   180→    margin: 0 0 var(--spacing-md) 0;
   181→    color: var(--text-muted);
   182→    text-transform: uppercase;
   183→    letter-spacing: 1px;
   184→}
   185→
   186→/* GO Button */
   187→.go-btn {
   188→    width: 100%;
   189→    padding: 16px;
   190→    font-size: 14px;
   191→    font-weight: 600;
   192→    background: var(--accent-gradient);
   193→    border: none;
   194→    border-radius: var(--radius-md);
   195→    color: white;
   196→    cursor: pointer;
   197→    margin-bottom: var(--spacing-sm);
   198→    letter-spacing: 0.5px;
   199→    transition: transform 0.2s var(--ease-smooth), box-shadow 0.2s;
   200→    box-shadow: var(--accent-glow);
   201→}
   202→
   203→.go-btn:hover {
   204→    transform: translateY(-2px);
   205→    box-shadow: 0 4px 15px rgba(74, 158, 255, 0.3);
   206→}
   207→
   208→.go-btn:active {
   209→    transform: translateY(0);
   210→}
   211→
   212→.go-btn:disabled {
   213→    opacity: 0.5;
   214→    cursor: not-allowed;
   215→    transform: none;
   216→    box-shadow: none;
   217→}
   218→
   219→/* Icon Buttons */
   220→.icon-btn {
   221→    background: transparent;
   222→    border: 1px solid var(--glass-border);
   223→    color: var(--text-muted);
   224→    width: 32px;
   225→    height: 32px;
   226→    border-radius: var(--radius-sm);
   227→    cursor: pointer;
   228→    font-size: 14px;
   229→    display: flex;
   230→    align-items: center;
   231→    justify-content: center;
   232→    transition: all 0.2s var(--ease-smooth);
   233→}
   234→
   235→.icon-btn:hover {
   236→    background: var(--glass-highlight);
   237→    color: var(--text-main);
   238→    border-color: var(--text-muted);
   239→}
   240→
   241→/* Options Toggle */
   242→.options-toggle {
   243→    background: transparent;
   244→    border: none;
   245→    color: var(--text-muted);
   246→    font-size: 10px;
   247→    cursor: pointer;
   248→    padding: 4px 0;
   249→    width: 100%;
   250→    text-align: left;
   251→}
   252→
   253→.options-toggle:hover {
   254→    color: #aaa;
   255→}
   256→
   257→.options-toggle::before {
   258→    content: '+';
   259→    margin-right: 4px;
   260→    font-weight: bold;
   261→}
   262→
   263→.options-toggle.expanded::before {
   264→    content: '-';
   265→}
   266→
   267→/* Options Panel */
   268→.options-panel {
   269→    margin-top: 8px;
   270→    padding-top: 8px;
   271→    border-top: 1px solid var(--glass-border);
   272→}
   273→
   274→.options-panel.collapsed,
   275→.sub-options.collapsed {
   276→    display: none;
   277→}
   278→
   279→/* Form Elements */
   280→.form-group {
   281→    margin-bottom: 10px;
   282→}
   283→
   284→.form-group label {
   285→    display: block;
   286→    font-size: 10px;
   287→    color: var(--text-muted);
   288→    margin-bottom: 4px;
   289→}
   290→
   291→.form-group.small {
   292→    margin-bottom: 6px;
   293→}
   294→
   295→.form-group.small label {
   296→    font-size: 9px;
   297→}
   298→
   299→input[type="text"],
   300→input[type="email"],
   301→input[type="number"],
   302→select {
   303→    width: 100%;
   304→    padding: 6px 8px;
   305→    background: var(--bg-panel);
   306→    border: 1px solid var(--glass-border);
   307→    border-radius: 4px;
   308→    color: #e1e1e1;
   309→    font-size: 11px;
   310→}
   311→
   312→input[type="text"]:focus,
   313→input[type="email"]:focus,
   314→input[type="number"]:focus,
   315→select:focus {
   316→    outline: none;
   317→    border-color: var(--accent-primary);
   318→}
   319→
   320→/* Slider */
   321→.slider-container {
   322→    margin-bottom: var(--spacing-md);
   323→}
   324→
   325→.slider-container label {
   326→    display: block;
   327→    font-size: 11px;
   328→    color: var(--text-muted);
   329→    margin-bottom: var(--spacing-xs);
   330→    font-weight: 500;
   331→}
   332→
   333→.slider-container.small {
   334→    margin-bottom: var(--spacing-sm);
   335→}
   336→
   337→input[type="range"] {
   338→    width: 100%;
   339→    height: 4px;
   340→    background: rgba(255, 255, 255, 0.1);
   341→    border-radius: 2px;
   342→    outline: none;
   343→    -webkit-appearance: none;
   344→    appearance: none;
   345→    /* Fixed lint: standard property */
   346→    cursor: pointer;
   347→}
   348→
   349→input[type="range"]::-webkit-slider-thumb {
   350→    -webkit-appearance: none;
   351→    appearance: none;
   352→    /* Fixed lint */
   353→    width: 16px;
   354→    height: 16px;
   355→    background: var(--accent-primary);
   356→    border-radius: 50%;
   357→    cursor: pointer;
   358→    box-shadow: 0 0 10px rgba(74, 158, 255, 0.4);
   359→    transition: transform 0.2s var(--ease-smooth);
   360→    margin-top: -6px;
   361→    /* Vertical centering adjustment */
   362→}
   363→
   364→input[type="range"]::-webkit-slider-thumb:hover {
   365→    transform: scale(1.2);
   366→}
   367→
   368→input[type="range"]::-webkit-slider-runnable-track {
   369→    width: 100%;
   370→    height: 4px;
   371→    cursor: pointer;
   372→    background: rgba(255, 255, 255, 0.1);
   373→    border-radius: 2px;
   374→}
   375→
   376→.slider-labels {
   377→    display: flex;
   378→    justify-content: space-between;
   379→    font-size: 10px;
   380→    color: var(--text-muted);
   381→    margin-top: var(--spacing-xs);
   382→}
   383→
   384→/* Checkbox */
   385→.checkbox-label {
   386→    display: flex;
   387→    align-items: center;
   388→    gap: 8px;
   389→    font-size: 11px;
   390→    color: var(--text-main);
   391→    cursor: pointer;
   392→    margin-bottom: var(--spacing-sm);
   393→    user-select: none;
   394→}
   395→
   396→.checkbox-label input[type="checkbox"] {
   397→    width: 16px;
   398→    height: 16px;
   399→    accent-color: var(--accent-primary);
   400→    background: rgba(0, 0, 0, 0.3);
   401→    border: 1px solid var(--glass-border);
   402→    border-radius: 4px;
   403→    cursor: pointer;
   404→}
   405→
   406→/* Tier Badge */
   407→.tier-badge {
   408→    font-size: 9px;
   409→    background: var(--accent-gradient);
   410→    color: white;
   411→    padding: 2px 6px;
   412→    border-radius: var(--radius-sm);
   413→    margin-left: var(--spacing-xs);
   414→    font-weight: 600;
   415→    box-shadow: var(--accent-glow);
   416→}
   417→
   418→/* Feature Toggles */
   419→.feature-toggles {
   420→    margin-top: 10px;
   421→}
   422→
   423→.sub-options {
   424→    margin-left: 20px;
   425→    margin-top: 6px;
   426→    padding: 8px;
   427→    background: rgba(0, 0, 0, 0.2);
   428→    border-radius: 4px;
   429→}
   430→
   431→/* Results Area */
   432→.results-area {
   433→    background: var(--bg-panel);
   434→    border-radius: 6px;
   435→    padding: 12px;
   436→    min-height: 60px;
   437→}
   438→
   439→.results-empty {
   440→    text-align: center;
   441→    color: #666;
   442→    padding: 20px;
   443→    font-size: 11px;
   444→}
   445→
   446→/* Progress */
   447→.progress-container {
   448→    text-align: center;
   449→    padding: var(--spacing-md) 0;
   450→}
   451→
   452→.progress-container.hidden {
   453→    display: none;
   454→}
   455→
   456→.progress-bar-container {
   457→    height: 6px;
   458→    background: rgba(0, 0, 0, 0.3);
   459→    border-radius: 3px;
   460→    overflow: hidden;
   461→    margin-bottom: var(--spacing-sm);
   462→    box-shadow: inset 0 1px 2px rgba(0, 0, 0, 0.3);
   463→}
   464→
   465→.progress-bar {
   466→    height: 100%;
   467→    background: var(--accent-gradient);
   468→    transition: width 0.3s var(--ease-smooth);
   469→    box-shadow: var(--accent-glow);
   470→}
   471→
   472→.progress-text {
   473→    font-size: 11px;
   474→    color: var(--text-muted);
   475→    font-weight: 500;
   476→}
   477→
   478→/* Preview Section */
   479→.preview-section {
   480→    margin-top: 8px;
   481→}
   482→
   483→.preview-section.hidden {
   484→    display: none;
   485→}
   486→
   487→.preview-header {
   488→    display: flex;
   489→    justify-content: space-between;
   490→    align-items: center;
   491→    margin-bottom: 8px;
   492→}
   493→
   494→.preview-header h3 {
   495→    font-size: 11px;
   496→    font-weight: 600;
   497→    color: #aaa;
   498→    text-transform: uppercase;
   499→}
   500→
   501→.preview-counts {
   502→    display: flex;
   503→    gap: 10px;
   504→    font-size: 10px;
   505→    color: #666;
   506→}
   507→
   508→.preview-counts span span {
   509→    color: var(--accent-primary);
   510→    font-weight: 600;
   511→}
   512→
   513→.preview-controls {
   514→    margin-bottom: 8px;
   515→}
   516→
   517→.preview-list {
   518→    max-height: 200px;
   519→    overflow-y: auto;
   520→    margin-bottom: var(--spacing-md);
   521→    padding-right: 4px;
   522→    /* Space for scrollbar */
   523→}
   524→
   525→.preview-item {
   526→    display: flex;
   527→    align-items: center;
   528→    padding: 8px 12px;
   529→    margin-bottom: 6px;
   530→    background: rgba(30, 30, 30, 0.4);
   531→    border: 1px solid var(--glass-border);
   532→    border-radius: var(--radius-sm);
   533→    border-left: 3px solid var(--accent-primary);
   534→    cursor: pointer;
   535→    transition: all 0.15s var(--ease-smooth);
   536→}
   537→
   538→.preview-item:hover {
   539→    background: rgba(255, 255, 255, 0.05);
   540→    transform: translateX(2px);
   541→}
   542→
   543→.preview-item.excluded {
   544→    opacity: 0.5;
   545→    border-left-color: var(--text-muted);
   546→    background: transparent;
   547→}
   548→
   549→.preview-item.take-item {
   550→    border-left-color: var(--success);
   551→}
   552→
   553→.preview-item-check {
   554→    min-width: 16px;
   555→    min-height: 16px;
   556→    margin-right: 10px;
   557→    accent-color: var(--accent-primary);
   558→}
   559→
   560→.preview-item-icon {
   561→    margin-right: 10px;
   562→    font-size: 14px;
   563→}
   564→
   565→.preview-item-info {
   566→    flex: 1;
   567→    min-width: 0;
   568→}
   569→
   570→.preview-item-time {
   571→    font-size: 11px;
   572→    font-weight: 500;
   573→    color: #e1e1e1;
   574→}
   575→
   576→.preview-item-duration,
   577→.preview-item-label {
   578→    font-size: 9px;
   579→    color: var(--text-muted);
   580→    margin-top: 2px;
   581→}
   582→
   583→.preview-actions {
   584→    display: flex;
   585→    gap: 8px;
   586→}
   587→
   588→/* Buttons */
   589→.btn {
   590→    padding: 8px 16px;
   591→    font-size: 12px;
   592→    font-weight: 500;
   593→    border: none;
   594→    border-radius: var(--radius-sm);
   595→    cursor: pointer;
   596→    transition: all 0.2s var(--ease-smooth);
   597→}
   598→
   599→.btn-primary {
   600→    background: var(--accent-primary);
   601→    color: white;
   602→    box-shadow: 0 2px 8px rgba(74, 158, 255, 0.2);
   603→}
   604→
   605→.btn-primary:hover {
   606→    background: #5aa8ff;
   607→    transform: translateY(-1px);
   608→    box-shadow: 0 4px 12px rgba(74, 158, 255, 0.3);
   609→}
   610→
   611→.btn-secondary {
   612→    background: rgba(255, 255, 255, 0.1);
   613→    color: var(--text-main);
   614→}
   615→
   616→.btn-secondary:hover {
   617→    background: rgba(255, 255, 255, 0.15);
   618→}
   619→
   620→.btn-ghost {
   621→    background: transparent;
   622→    color: var(--text-muted);
   623→    border: 1px solid var(--glass-border);
   624→}
   625→
   626→.btn-ghost:hover {
   627→    background: var(--glass-highlight);
   628→    color: var(--text-main);
   629→    border-color: var(--text-muted);
   630→}
   631→
   632→.btn-small {
   633→    padding: 6px 10px;
   634→    font-size: 11px;
   635→}
   636→
   637→/* Chapter Results */
   638→.chapter-results,
   639→.zoom-results {
   640→    margin-top: 12px;
   641→    padding-top: 12px;
   642→    border-top: 1px solid #333;
   643→}
   644→
   645→.chapter-results.hidden,
   646→.zoom-results.hidden {
   647→    display: none;
   648→}
   649→
   650→.chapter-header,
   651→.zoom-header {
   652→    display: flex;
   653→    justify-content: space-between;
   654→    align-items: center;
   655→    margin-bottom: 8px;
   656→}
   657→
   658→.chapter-header h3,
   659→.zoom-header h3 {
   660→    font-size: 11px;
   661→    font-weight: 600;
   662→    color: #aaa;
   663→    text-transform: uppercase;
   664→}
   665→
   666→.chapter-actions {
   667→    display: flex;
   668→    gap: 4px;
   669→}
   670→
   671→.chapter-list,
   672→.zoom-list {
   673→    max-height: 120px;
   674→    overflow-y: auto;
   675→}
   676→
   677→.chapter-item {
   678→    display: flex;
   679→    gap: 10px;
   680→    padding: 6px 8px;
   681→    background: var(--bg-card);
   682→    border-radius: 4px;
   683→    margin-bottom: 4px;
   684→    cursor: pointer;
   685→    transition: background 0.15s;
   686→}
   687→
   688→.chapter-item:hover {
   689→    background: #333;
   690→}
   691→
   692→.chapter-time {
   693→    font-size: 10px;
   694→    color: var(--accent-primary);
   695→    font-weight: 500;
   696→    min-width: 50px;
   697→}
   698→
   699→.chapter-title {
   700→    font-size: 10px;
   701→    color: #e1e1e1;
   702→    flex: 1;
   703→    white-space: nowrap;
   704→    overflow: hidden;
   705→    text-overflow: ellipsis;
   706→}
   707→
   708→/* Modal */
   709→.modal {
   710→    position: fixed;
   711→    top: 0;
   712→    left: 0;
   713→    width: 100%;
   714→    height: 100%;
   715→    background: rgba(0, 0, 0, 0.6);
   716→    backdrop-filter: blur(4px);
   717→    display: flex;
   718→    align-items: center;
   719→    justify-content: center;
   720→    z-index: 1000;
   721→    animation: fadeIn 0.2s ease-out;
   722→}
   723→
   724→.modal.hidden {
   725→    display: none;
   726→}
   727→
   728→.modal-content {
   729→    background: var(--bg-panel);
