  { h: 45, o: 1 }, { h: 72, o: 0.5 }, { h: 38, o: 1 }, { h: 85, o: 0.5 }, { h: 52, o: 1 },
  { h: 28, o: 0.5 }, { h: 67, o: 1 }, { h: 43, o: 0.5 }, { h: 91, o: 1 }, { h: 35, o: 0.5 },
  { h: 78, o: 1 }, { h: 55, o: 0.5 }, { h: 32, o: 1 }, { h: 88, o: 0.5 }, { h: 48, o: 1 },
  { h: 25, o: 0.5 }, { h: 73, o: 1 }, { h: 62, o: 0.5 }, { h: 95, o: 1 }, { h: 40, o: 0.5 },
  { h: 58, o: 1 }, { h: 82, o: 0.5 }, { h: 36, o: 1 }, { h: 69, o: 0.5 }, { h: 50, o: 1 },
  { h: 29, o: 0.5 }, { h: 76, o: 1 }, { h: 44, o: 0.5 }, { h: 87, o: 1 }, { h: 33, o: 0.5 },
  { h: 65, o: 1 }, { h: 53, o: 0.5 }, { h: 92, o: 1 }, { h: 41, o: 0.5 }, { h: 70, o: 1 },
  { h: 27, o: 0.5 }, { h: 80, o: 1 }, { h: 57, o: 0.5 }, { h: 34, o: 1 }, { h: 83, o: 0.5 },
  { h: 46, o: 1 }, { h: 68, o: 0.5 }, { h: 39, o: 1 }, { h: 90, o: 0.5 }, { h: 54, o: 1 },
  { h: 31, o: 0.5 }, { h: 75, o: 1 }, { h: 60, o: 0.5 }, { h: 86, o: 1 }, { h: 42, o: 0.5 },
  { h: 63, o: 1 }, { h: 79, o: 0.5 }, { h: 37, o: 1 }, { h: 71, o: 0.5 }, { h: 49, o: 1 },
  { h: 26, o: 0.5 }, { h: 84, o: 1 }, { h: 56, o: 0.5 }, { h: 93, o: 1 }, { h: 47, o: 0.5 },
];

export function Hero() {
  const [showDemo, setShowDemo] = useState(false);

  return (
    <section className="relative min-h-screen flex items-center justify-center pt-20 pb-16 overflow-hidden dark">
      {/* Background gradient */}
      <div className="absolute inset-0 hero-gradient" />

      {/* Grid pattern */}
      <div className="absolute inset-0 grid-pattern opacity-50" />

      {/* Animated background elements - hidden on mobile for performance */}
      <div className="absolute inset-0 overflow-hidden hidden sm:block">
        <motion.div
          className="absolute top-1/4 left-1/4 w-[500px] h-[500px] bg-[#00D4FF]/10 rounded-full blur-[100px] will-change-transform"
          animate={{
            scale: [1, 1.2, 1],
            opacity: [0.3, 0.5, 0.3],
          }}
          transition={{
            duration: 8,
            repeat: Infinity,
            ease: "easeInOut",
