   109→
   110→              {/* Gradient header */}
   111→              <div className="bg-gradient-to-r from-[#00D4FF]/20 to-purple-500/20 p-8 pb-6">
   112→                <div className="flex items-center gap-3 mb-4">
   113→                  <div className="p-3 rounded-full bg-[#00D4FF]/20">
   114→                    <Gift className="w-6 h-6 text-[#00D4FF]" />
   115→                  </div>
