   214→// ============================================================================
   215→// SEQUENCE EVENT LISTENERS
   216→// ============================================================================
   217→/**
   218→ * Initialize event listeners for sequence state changes
   219→ * This provides real-time updates when sequences are activated/deactivated
   220→ */
   221→function initSequenceEventListeners() {
   222→    if (!jsx.cs) {
   223→        console.warn('[SPLICE] Cannot init sequence listeners - CSInterface not available');
   224→        return;
   225→    }
   226→
   227→    try {
   228→        // Listen for sequence activation events
   229→        jsx.cs.addEventListener('com.adobe.csxs.events.SequenceActivated', async (event) => {
   230→            debugLog('Sequence activated event received');
   231→            try {
   232→                const seqInfo = await jsx.call('getActiveSequence');
   233→                if (seqInfo && !seqInfo.error) {
   234→                    window.spliceState.activeSequence = seqInfo;
   235→                    setStatus('Sequence: ' + (seqInfo.name || 'Unknown'));
   236→                    debugLog('Active sequence updated:', seqInfo.name);
   237→                }
   238→            } catch (e) {
   239→                console.warn('[SPLICE] Failed to get sequence info on activation:', e.message);
   240→            }
   241→        });
   242→
   243→        // Listen for sequence close/deactivation
   244→        jsx.cs.addEventListener('com.adobe.csxs.events.SequenceDeactivated', () => {
   245→            debugLog('Sequence deactivated event');
   246→            window.spliceState.activeSequence = null;
   247→            setStatus('No sequence open', true);
   248→        });
   249→
   250→        // Listen for project item selection changes (includes sequences)
   251→        jsx.cs.addEventListener('com.adobe.csxs.events.SelectionChanged', async () => {
   252→            // Debounce rapid selection changes
   253→            if (window._selectionChangeTimeout) {
   254→                clearTimeout(window._selectionChangeTimeout);
   255→            }
   256→            window._selectionChangeTimeout = setTimeout(async () => {
   257→                try {
   258→                    const hasSequence = await checkSequenceOpen(0, 0); // Quick check, no retries
   259→                    if (hasSequence && !window.spliceState.activeSequence) {
   260→                        const seqInfo = await jsx.call('getActiveSequence');
   261→                        if (seqInfo && !seqInfo.error) {
   262→                            window.spliceState.activeSequence = seqInfo;
   263→                            debugLog('Sequence detected via selection change:', seqInfo.name);
   264→                        }
   265→                    }
   266→                } catch (e) {
   267→                    // Ignore errors during selection change - it's just for proactive updates
   268→                }
   269→            }, 200);
   270→        });
   271→
   272→        debugLog('Sequence event listeners initialized');
   273→    } catch (e) {
   274→        console.error('[SPLICE] Failed to init sequence event listeners:', e.message);
   275→    }
   276→}
