   278→// ============================================================================
   279→// JSX CONNECTION CHECK
   280→// ============================================================================
   281→async function checkJSXConnection() {
   282→    try {
   283→        const result = await jsx.call('getVersion');
   284→        debugLog('JSX connection OK, version:', result);
   285→        return true;
   286→    } catch (e) {
   287→        console.warn('[SPLICE] JSX connection failed:', e.message);
   288→        setStatus('Premiere Pro connection failed', true);
   289→        return false;
   290→    }
   291→}
