   293→/**
   294→ * Check if a sequence is currently open in the Timeline
   295→ * Enhanced with logging and retry logic
   296→ * @param {number} retries - Number of retry attempts (default: 2)
   297→ * @param {number} delayMs - Delay between retries in ms (default: 300)
   298→ * @returns {Promise<boolean>} true if sequence is open
   299→ */
   300→async function checkSequenceOpen(retries = 2, delayMs = 300) {
   301→    console.log('[SPLICE] ========== checkSequenceOpen DIAGNOSTIC START ==========');
   302→    console.log('[SPLICE] checkSequenceOpen: retries=' + retries + ', delayMs=' + delayMs);
   303→    console.log('[SPLICE] checkSequenceOpen: window.spliceState.activeSequence =', window.spliceState.activeSequence);
   304→    
   305→    for (let attempt = 0; attempt <= retries; attempt++) {
   306→        try {
   307→            console.log('[SPLICE] checkSequenceOpen: Attempt ' + (attempt + 1) + ' of ' + (retries + 1));
   308→            console.log('[SPLICE] checkSequenceOpen: Calling jsx.call("checkSequenceOpen")...');
   309→            const result = await jsx.call('checkSequenceOpen');
   310→            console.log('[SPLICE] checkSequenceOpen: JSX call returned');
   311→            console.log('[SPLICE] checkSequenceOpen: result =', result, '(type:', typeof result + ')');
   312→            debugLog('checkSequenceOpen attempt', attempt + 1, 'result:', result, 'type:', typeof result);
   313→
   314→            if (result === true || result === 'true') {
   315→                console.log('[SPLICE] checkSequenceOpen: Returning TRUE - sequence is open');
   316→                console.log('[SPLICE] ========== checkSequenceOpen DIAGNOSTIC END (TRUE) ==========');
   317→                return true;
   318→            }
   319→
   320→            // If false, wait and retry (sequence might be loading)
   321→            if (attempt < retries) {
   322→                console.log('[SPLICE] checkSequenceOpen: No sequence found, retrying in', delayMs, 'ms...');
   323→                debugLog('checkSequenceOpen: No sequence found, retrying in', delayMs, 'ms...');
   324→                await new Promise(r => setTimeout(r, delayMs));
   325→            }
   326→        } catch (error) {
   327→            console.error('[SPLICE] checkSequenceOpen: Attempt', attempt + 1, 'failed with exception:', error.message);
   328→            console.error('[SPLICE] checkSequenceOpen: Error stack:', error.stack);
   329→
   330→            // On last attempt, return false but log the actual error
   331→            if (attempt === retries) {
   332→                console.error('[SPLICE] checkSequenceOpen: All attempts failed. Last error:', error.message);
   333→                // Store error for diagnostics
   334→                window.spliceState.lastSequenceCheckError = error.message;
   335→                console.log('[SPLICE] checkSequenceOpen: Stored error in window.spliceState.lastSequenceCheckError:', error.message);
   336→                console.log('[SPLICE] ========== checkSequenceOpen DIAGNOSTIC END (ERROR) ==========');
   337→                return false;
   338→            }
   339→
   340→            // Wait before retry
   341→            console.log('[SPLICE] checkSequenceOpen: Waiting', delayMs, 'ms before retry...');
   342→            await new Promise(r => setTimeout(r, delayMs));
   343→        }
   344→    }
   345→    console.log('[SPLICE] checkSequenceOpen: All attempts completed without finding sequence');
   346→    console.log('[SPLICE] ========== checkSequenceOpen DIAGNOSTIC END (FALSE) ==========');
   347→    return false;
   348→}
