   353→function initOptionsToggle() {
   354→    debugLog('initOptionsToggle - toggle:', !!ui.optionsToggle, 'panel:', !!ui.optionsPanel);
   355→    if (ui.optionsToggle && ui.optionsPanel) {
   356→        ui.optionsToggle.addEventListener('click', () => {
   357→            debugLog('Options toggle clicked');
   358→            // Check current state BEFORE toggling
   359→            const wasCollapsed = ui.optionsPanel.classList.contains('collapsed');
   360→            ui.optionsPanel.classList.toggle('collapsed');
   361→            // If it was collapsed, it's now expanded (show 'expanded' class)
   362→            ui.optionsToggle.classList.toggle('expanded', wasCollapsed);
   363→            saveSettings({ expandedOptions: wasCollapsed });
   364→        });
   365→
   366→        // Restore state
   367→        const settings = getSettings();
   368→        if (!settings.expandedOptions) {
   369→            ui.optionsPanel.classList.add('collapsed');
   370→            ui.optionsToggle.classList.remove('expanded');
   371→        } else {
   372→            ui.optionsToggle.classList.add('expanded');
   373→            ui.optionsPanel.classList.remove('collapsed');
   374→        }
   375→    }
   376→}
