   541→// ============================================================================
   542→// PRESETS is defined in config.js
   543→
   544→function initPresetSelector() {
   545→    if (!ui.presetSelector) return;
   546→
   547→    ui.presetSelector.addEventListener('change', () => {
   548→        const presetId = ui.presetSelector.value;
   549→        applyPreset(presetId);
   550→    });
   551→
   552→    // Load current preset
   553→    const settings = getSettings();
   554→    if (settings.activePreset) {
   555→        ui.presetSelector.value = settings.activePreset;
   556→    }
   557→}
   558→
   559→function applyPreset(presetId) {
   560→    const preset = PRESETS[presetId];
   561→    if (!preset) return;
   562→
   563→    // Custom preset - keep current settings
   564→    if (presetId === 'custom' || !preset.settings) {
   565→        saveSettings({ activePreset: 'custom' });
   566→        setStatus('Using custom settings');
   567→        return;
   568→    }
   569→
   570→    const settings = preset.settings;
   571→
   572→    // Apply to UI
   573→    if (ui.sensitivitySlider && settings.sensitivity !== undefined) {
   574→        ui.sensitivitySlider.value = settings.sensitivity;
   575→        if (ui.sensitivityValue) ui.sensitivityValue.textContent = settings.sensitivity;
   576→    }
   577→
   578→    if (ui.enableJCut) {
   579→        ui.enableJCut.checked = settings.enableJCut || false;
   580→        if (ui.jcutSettings) {
   581→            ui.jcutSettings.classList.toggle('collapsed', !settings.enableJCut);
   582→        }
   583→    }
   584→
   585→    if (settings.jCutLeadIn !== undefined && ui.jcutLeadIn) {
