   658→async function runDetection() {
   659→    if (isOperationInProgress) return;
   660→
   661→    // Check sequence with enhanced error messaging
   662→    const hasSequence = await checkSequenceOpen();
   663→    if (!hasSequence) {
   664→        // Check if there was a communication error vs just no sequence
   665→        const lastError = window.spliceState.lastSequenceCheckError;
   666→        if (lastError && (lastError.includes('CEP') || lastError.includes('communication') || lastError.includes('evalScript'))) {
   667→            setStatus('Premiere Pro connection issue - try reopening panel', true);
   668→            console.error('[SPLICE] runDetection: JSX communication failure:', lastError);
   669→        } else {
   670→            setStatus('Please open a sequence in the Timeline first', true);
   671→            debugLog('runDetection: No active sequence in Timeline');
   672→        }
   673→        // Clear the error for next check
   674→        window.spliceState.lastSequenceCheckError = null;
   675→        return;
   676→    }
   677→
   678→    // Check online
   679→    if (!isOnline()) {
   680→        setStatus('Offline - Check your connection', true);
   681→        return;
   682→    }
   683→
   684→    isOperationInProgress = true;
   685→    ui.goBtn.disabled = true;
   686→    showProgress('Detecting silences...');
   687→
   688→    try {
   689→        const settings = getSettings();
   690→
   691→        // Get sequence info
   692→        const seqInfo = await jsx.call('getActiveSequence');
   693→        debugLog('Sequence info:', seqInfo);
   694→
   695→        // Store sequence info for later use
   696→        currentSequenceInfo = seqInfo;
   697→        window.spliceState.activeSequence = seqInfo;
   698→        if (seqInfo && seqInfo.videoPath) {
   699→            window.spliceState.currentVideoPath = seqInfo.videoPath;
   700→        }
   701→
   702→        // Export audio via JSX for silence detection
   703→        updateProgress(10, 'Exporting audio...');
   704→
   705→        let audioFilePath = null;
   706→        try {
   707→            // Get sequence info for audio export
   708→            const exportResult = await jsx.call('exportSequenceAudioForAnalysis');
   709→            if (exportResult && exportResult.success && exportResult.outputPath) {
   710→                audioFilePath = exportResult.outputPath;
   711→                debugLog('Audio exported to:', audioFilePath);
   712→            } else if (exportResult && exportResult.error) {
   713→                throw new Error(exportResult.error);
   714→            }
   715→        } catch (exportError) {
   716→            console.warn('[SPLICE] Audio export failed, trying fallback:', exportError.message);
   717→            // Try to get audio from first clip in timeline
   718→            try {
   719→                const clipInfo = await jsx.call('getFirstClipAudioPath');
   720→                if (clipInfo && clipInfo.path) {
   721→                    audioFilePath = clipInfo.path;
   722→                    debugLog('Using clip audio path:', audioFilePath);
   723→                }
   724→            } catch (clipError) {
   725→                console.warn('[SPLICE] Fallback audio path failed:', clipError.message);
   726→            }
   727→        }
   728→
   729→        if (!audioFilePath) {
   730→            throw new Error('Could not export or locate audio file. Please ensure sequence has audio tracks.');
   731→        }
   732→
   733→        // Store audio path globally for other modules
   734→        currentAudioPath = audioFilePath;
   735→        window.spliceState.audioPath = audioFilePath;
   736→
   737→        // Call backend for silence detection with audio file path
   738→        updateProgress(30, 'Detecting silences...');
   739→        const silenceResponse = await fetchWithTimeout(
   740→            `${getBackendUrl()}/silences-rms`,
   741→            {
   742→                method: 'POST',
   743→                headers: getAuthHeaders(),
   744→                body: JSON.stringify({
   745→                    wavPath: audioFilePath,
   746→                    sensitivity: settings.sensitivity,
   747→                    minSilenceLength: settings.minSilenceLength || 0.5
   748→                })
   749→            }
   750→        );
   751→
   752→        if (!silenceResponse.ok) {
   753→            throw new Error(await parseErrorResponse(silenceResponse));
   754→        }
   755→
   756→        const silenceData = await silenceResponse.json();
   757→        previewSilences = silenceData.silences || [];
   758→
   759→        // Detect takes if enabled
   760→        if (settings.enableTakesDetection) {
   761→            updateProgress(60, 'Detecting takes...');
   762→            try {
   763→                const analyzeResponse = await fetchWithTimeout(
   764→                    `${getBackendUrl()}/analyze`,
   765→                    {
   766→                        method: 'POST',
   767→                        headers: getAuthHeaders(),
   768→                        body: JSON.stringify({
   769→                            wavPath: audioFilePath,
   770→                            detectTakes: true
   771→                        })
   772→                    }
   773→                );
   774→
   775→                if (analyzeResponse.ok) {
   776→                    const analyzeData = await analyzeResponse.json();
   777→                    previewTakes = analyzeData.takes || [];
   778→
   779→                    // Store transcript for captions/music modules
   780→                    if (analyzeData.transcript) {
   781→                        currentTranscript = analyzeData.transcript;
   782→                        window.currentTranscript = analyzeData.transcript;
   783→                        window.spliceState.lastTranscript = analyzeData.transcript;
   784→                    }
   785→                } else {
   786→                    // Handle specific error codes
   787→                    const errorMsg = await parseErrorResponse(analyzeResponse);
   788→                    if (analyzeResponse.status === 402) {
   789→                        console.warn('[SPLICE] Takes detection skipped: insufficient credits');
   790→                        setStatus('Takes detection skipped: insufficient credits', true);
   791→                    } else if (analyzeResponse.status === 401) {
   792→                        console.warn('[SPLICE] Takes detection skipped: authentication required');
   793→                    } else {
   794→                        console.warn('[SPLICE] Takes detection failed:', errorMsg);
   795→                    }
   796→                }
   797→            } catch (e) {
   798→                console.warn('[SPLICE] Takes detection failed:', e.message);
   799→            }
   800→        }
   801→
   802→        // Detect profanity if enabled
   803→        if (settings.enableProfanity) {
   804→            updateProgress(65, 'Detecting profanity...');
   805→            try {
   806→                const profanityResponse = await fetchWithTimeout(
   807→                    `${getBackendUrl()}/profanity`,
   808→                    {
   809→                        method: 'POST',
   810→                        headers: getAuthHeaders(),
   811→                        body: JSON.stringify({
   812→                            wavPath: audioFilePath,
   813→                            language: settings.profanityLanguage || 'en',
   814→                            bleepType: settings.bleepType || 'standard'
   815→                        })
   816→                    }
   817→                );
   818→
   819→                if (profanityResponse.ok) {
   820→                    const profanityData = await profanityResponse.json();
   821→                    // Backend returns 'words' array, not 'instances'
   822→                    currentProfanity = profanityData.words || profanityData.instances || [];
   823→                    if (currentProfanity.length > 0) {
   824→                        debugLog(`Found ${currentProfanity.length} profanity instances`);
   825→                    }
   826→                } else {
   827→                    console.warn('[SPLICE] Profanity detection failed:', await parseErrorResponse(profanityResponse));
   828→                }
   829→            } catch (e) {
   830→                console.warn('[SPLICE] Profanity detection failed:', e.message);
   831→            }
   832→        }
   833→
   834→        // Detect chapters if enabled
   835→        if (settings.enableChapters) {
   836→            updateProgress(80, 'Detecting chapters...');
   837→            try {
   838→                const chapterResponse = await fetchWithTimeout(
   839→                    `${getBackendUrl()}/chapters`,
   840→                    {
   841→                        method: 'POST',
   842→                        headers: getAuthHeaders(),
   843→                        body: JSON.stringify({
   844→                            wavPath: audioFilePath,
   845→                            transcript: currentTranscript,
   846→                            maxChapters: settings.maxChapters || 10,
   847→                            minChapterLength: settings.minChapterLength || 60
   848→                        })
   849→                    }
   850→                );
   851→
   852→                if (chapterResponse.ok) {
   853→                    const chapterData = await chapterResponse.json();
   854→                    currentChapters = chapterData.chapters || [];
   855→                } else {
   856→                    const errorMsg = await parseErrorResponse(chapterResponse);
   857→                    console.warn('[SPLICE] Chapter detection failed:', errorMsg);
   858→                    // Don't block workflow, just notify user
   859→                    setStatus('Chapter detection skipped: ' + errorMsg, true);
   860→                }
   861→            } catch (e) {
   862→                console.warn('[SPLICE] Chapter detection failed:', e);
   863→                setStatus('Chapter detection failed - continuing without chapters', true);
   864→            }
   865→        }
   866→
   867→        // Detect zoom points if enabled
   868→        if (settings.enableZoom) {
   869→            updateProgress(90, 'Detecting zoom points...');
   870→            try {
   871→                const zoomResponse = await fetchWithTimeout(
   872→                    `${getBackendUrl()}/zoom`,
   873→                    {
   874→                        method: 'POST',
   875→                        headers: getAuthHeaders(),
   876→                        body: JSON.stringify({
   877→                            wavPath: audioFilePath,
   878→                            transcript: currentTranscript,
   879→                            frequency: settings.zoomFrequency || 'medium',
   880→                            preset: settings.zoomPreset || 'medium',
   881→                            placement: settings.zoomPlacement || 'sentence_start'
   882→                        })
   883→                    }
   884→                );
   885→
   886→                if (zoomResponse.ok) {
   887→                    const zoomData = await zoomResponse.json();
   888→                    currentZooms = zoomData.zoomPoints || [];
   889→                } else {
   890→                    const errorMsg = await parseErrorResponse(zoomResponse);
   891→                    console.warn('[SPLICE] Zoom detection failed:', errorMsg);
   892→                    setStatus('Zoom detection skipped: ' + errorMsg, true);
   893→                }
   894→            } catch (e) {
   895→                console.warn('[SPLICE] Zoom detection failed:', e);
   896→                setStatus('Zoom detection failed - continuing without zoom points', true);
   897→            }
   898→        }
   899→
   900→        // Store results
   901→        lastDetectionResult = {
   902→            silences: previewSilences,
   903→            takes: previewTakes,
   904→            chapters: currentChapters,
   905→            zooms: currentZooms
   906→        };
   907→
   908→        // Show preview
   909→        hideProgress();
   910→        showCombinedPreview();
   911→        setStatus(`Found ${previewSilences.length} silences, ${previewTakes.length} takes`);
   912→
   913→    } catch (error) {
   914→        console.error('[SPLICE] Detection error:', error);
   915→        setStatus('Detection failed: ' + error.message, true);
   916→        hideProgress();
   917→    } finally {
   918→        isOperationInProgress = false;
   919→        ui.goBtn.disabled = false;
   920→    }
   921→}
