   975→function renderPreviewList() {
   976→    if (!ui.previewList) return;
   977→
   978→    const html = [];
   979→
   980→    // Render silences
   981→    previewSilences.forEach((silence, i) => {
   982→        const isSelected = selectedSilenceIndices.has(i);
   983→        const duration = silence.end - silence.start;
   984→        html.push(`
   985→            <div class="preview-item ${isSelected ? '' : 'excluded'}" data-index="${i}" data-type="silence">
   986→                <input type="checkbox" class="preview-item-check" ${isSelected ? 'checked' : ''}>
   987→                <div class="preview-item-info">
   988→                    <div class="preview-item-time">${formatTime(silence.start)} - ${formatTime(silence.end)}</div>
   989→                    <div class="preview-item-duration">${duration.toFixed(2)}s silence</div>
   990→                </div>
