   992→        `);
   993→    });
   994→
   995→    // Render takes
   996→    previewTakes.forEach((take, i) => {
   997→        // SECURITY: Escape take label to prevent XSS
   998→        const safeLabel = escapeHtml(take.label || 'Take ' + (take.takeNumber || i + 1));
   999→        html.push(`
  1000→            <div class="preview-item take-item" data-index="${i}" data-type="take">
  1001→                <div class="preview-item-icon">🎬</div>
  1002→                <div class="preview-item-info">
  1003→                    <div class="preview-item-time">${formatTime(take.start)} - ${formatTime(take.end)}</div>
  1004→                    <div class="preview-item-label">${safeLabel}</div>
  1005→                </div>
  1006→            </div>
  1007→        `);
  1008→    });
  1009→
  1010→    ui.previewList.innerHTML = html.join('');
  1011→
  1012→    // Add click handlers
  1013→    ui.previewList.querySelectorAll('.preview-item').forEach(item => {
  1014→        item.addEventListener('click', (e) => {
  1015→            const index = parseInt(item.dataset.index);
  1016→            const type = item.dataset.type;
  1017→
  1018→            if (e.target.classList.contains('preview-item-check')) {
  1019→                // Checkbox clicked
  1020→                toggleSilenceSelection(index);
  1021→            } else {
  1022→                // Item clicked - seek to time
