  1049→async function seekToTime(seconds) {
  1050→    try {
  1051→        // Check if there's an active sequence before seeking
  1052→        if (!app?.project?.activeSequence) {
  1053→            debugLog('No active sequence to seek in');
  1054→            return;
  1055→        }
  1056→        // Use JSX to set playhead position
  1057→        // Note: setPlayerPosition expects ticks as a string without quotes around the number value
  1058→        const ticks = Math.round(seconds * 254016000000);
  1059→        await jsx.evalScript(`app.project.activeSequence.setPlayerPosition(${ticks})`);
  1060→    } catch (e) {
  1061→        console.warn('[SPLICE] Seek failed:', e);
  1062→    }
  1063→}
