  1142→async function applyPreviewAndMarkers() {
  1143→    if (isOperationInProgress) return;
  1144→
  1145→    isOperationInProgress = true;
  1146→    setStatus('Adding markers...');
  1147→
  1148→    try {
  1149→        // Add silence markers
  1150→        const selectedSilences = previewSilences.filter((_, i) => selectedSilenceIndices.has(i));
  1151→        for (const silence of selectedSilences) {
  1152→            await jsx.call('createMarker', silence.start, 'SPLICE: Silence', silence.end - silence.start, null, 1);
  1153→        }
  1154→
  1155→        // Add take markers
  1156→        for (const take of previewTakes) {
  1157→            await jsx.call('createMarker', take.start, take.label || `Take ${take.takeNumber}`, take.end - take.start, null, 5);
  1158→        }
  1159→
  1160→        setStatus(`Added ${selectedSilences.length + previewTakes.length} markers`);
  1161→    } catch (error) {
  1162→        setStatus('Failed to add markers: ' + error.message, true);
  1163→    } finally {
  1164→        isOperationInProgress = false;
  1165→    }
  1166→}
