  1178→async function buildSequence() {
  1179→    if (isOperationInProgress) return;
  1180→
  1181→    isOperationInProgress = true;
  1182→    setStatus('Building sequence...');
  1183→
  1184→    try {
  1185→        const settings = getSettings();
  1186→
  1187→        // Ensure we have sequence info
  1188→        if (!currentSequenceInfo) {
  1189→            currentSequenceInfo = await jsx.call('getActiveSequence');
  1190→        }
  1191→
  1192→        // Create cut list from selected silences
  1193→        const selectedSilences = previewSilences.filter((_, i) => selectedSilenceIndices.has(i));
  1194→
  1195→        // Calculate total duration from sequence info or last silence
  1196→        let duration = 0;
  1197→        if (currentSequenceInfo && currentSequenceInfo.duration) {
  1198→            duration = currentSequenceInfo.duration;
  1199→        } else if (previewSilences.length > 0) {
  1200→            const lastSilence = previewSilences[previewSilences.length - 1];
  1201→            duration = lastSilence.end + 10; // Add buffer
  1202→        }
  1203→
  1204→        // Call backend to generate cut list
  1205→        const cutListResponse = await fetchWithTimeout(
  1206→            `${getBackendUrl()}/cut-list`,
  1207→            {
  1208→                method: 'POST',
  1209→                headers: getAuthHeaders(),
  1210→                body: JSON.stringify({
  1211→                    sourceName: currentSequenceInfo?.name || 'Untitled Sequence',
  1212→                    sourcePath: currentSequenceInfo?.treePath || currentSequenceInfo?.name || 'Untitled',
  1213→                    duration: duration,
  1214→                    silences: selectedSilences,
  1215→                    takes: previewTakes,
  1216→                    enableTakes: settings.enableTakesDetection,
  1217→                    jCutOffset: settings.enableJCut ? settings.jcutLeadIn : 0,
  1218→                    lCutOffset: settings.enableJCut ? settings.jcutLeadOut : 0
  1219→                })
  1220→            }
  1221→        );
  1222→
  1223→        if (!cutListResponse.ok) {
  1224→            throw new Error(await parseErrorResponse(cutListResponse));
  1225→        }
  1226→
  1227→        const cutList = await cutListResponse.json();
  1228→
  1229→        // Build sequence via JSX
  1230→        const result = await jsx.call('buildSequenceFromCutList', JSON.stringify(cutList));
  1231→
  1232→        if (result && result.success) {
  1233→            setStatus(`Built sequence: ${result.sequenceName}`);
  1234→            hidePreview();
  1235→        } else {
  1236→            throw new Error(result?.error || 'Build failed');
  1237→        }
  1238→    } catch (error) {
  1239→        setStatus('Build failed: ' + error.message, true);
  1240→    } finally {
  1241→        isOperationInProgress = false;
  1242→    }
  1243→}
