  1388→/**
  1389→ * ACCESSIBILITY: Announce message to screen readers via ARIA live region
  1390→ * @param {string} message - Message to announce
  1391→ * @param {string} priority - 'polite' or 'assertive'
  1392→ */
  1393→function announceToScreenReader(message, priority = 'polite') {
  1394→    // Use existing status element or create temporary one
  1395→    const statusEl = document.getElementById('status');
  1396→    if (statusEl && priority === 'polite') {
  1397→        const originalText = statusEl.textContent;
  1398→        statusEl.textContent = message;
  1399→        // Restore after announcement
  1400→        setTimeout(() => {
  1401→            if (statusEl.textContent === message) {
  1402→                statusEl.textContent = originalText;
  1403→            }
  1404→        }, 1000);
  1405→        return;
  1406→    }
  1407→
  1408→    // Create temporary live region for assertive announcements
  1409→    const liveRegion = document.createElement('div');
  1410→    liveRegion.setAttribute('role', 'alert');
  1411→    liveRegion.setAttribute('aria-live', priority);
  1412→    liveRegion.setAttribute('aria-atomic', 'true');
  1413→    liveRegion.className = 'sr-only';
  1414→    liveRegion.style.cssText = 'position:absolute;width:1px;height:1px;padding:0;margin:-1px;overflow:hidden;clip:rect(0,0,0,0);border:0;';
  1415→    liveRegion.textContent = message;
  1416→    document.body.appendChild(liveRegion);
  1417→
  1418→    // Remove after announcement
  1419→    setTimeout(() => liveRegion.remove(), 1000);
  1420→}
