  1501→async function lookupLicense() {
  1502→    const lookupEmailInput = document.getElementById('lookupEmailInput');
  1503→    const licenseKeyInput = document.getElementById('licenseKeyInput');
  1504→
  1505→    if (!lookupEmailInput) return;
  1506→
  1507→    const email = lookupEmailInput.value.trim().toLowerCase();
  1508→    if (!email) {
  1509→        setStatus('Please enter your email address', true);
  1510→        return;
  1511→    }
  1512→
  1513→    // Basic email validation
  1514→    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  1515→    if (!emailRegex.test(email)) {
  1516→        setStatus('Please enter a valid email address', true);
  1517→        return;
  1518→    }
  1519→
  1520→    const lookupBtn = document.getElementById('lookupLicenseBtn');
  1521→    if (lookupBtn) {
  1522→        lookupBtn.disabled = true;
  1523→        lookupBtn.textContent = 'Looking up...';
  1524→    }
  1525→
  1526→    try {
  1527→        const response = await fetchWithTimeout(
  1528→            `${getBackendUrl()}/license/lookup`,
  1529→            {
  1530→                method: 'POST',
  1531→                headers: { 'Content-Type': 'application/json' },
  1532→                body: JSON.stringify({ email })
  1533→            },
  1534→            30000
  1535→        );
  1536→
  1537→        const result = await response.json();
  1538→
  1539→        // Note: For security, the backend does NOT return the license key directly.
  1540→        // It sends the key via email to prevent enumeration attacks.
  1541→        if (result.success) {
  1542→            setStatus(result.message || 'If a license exists, it has been sent to your email.');
