  1660→async function runDiagnostics() {
  1661→    if (!ui.debugLogs || !ui.debugQuickDiagnosis) return;
  1662→
  1663→    diagnosticLogs = [];
  1664→    ui.debugLogs.innerHTML = '<p class="debug-running">Running diagnostics... Please wait...</p>';
  1665→
  1666→    try {
  1667→        // Test 0: Check CEP environment
  1668→        addDiagnosticLog('Test 0: Checking CEP environment...');
  1669→        if (window.__adobe_cep__) {
  1670→            addDiagnosticLog('✓ CEP runtime detected (window.__adobe_cep__ exists)');
  1671→        } else {
  1672→            addDiagnosticLog('✗ CEP runtime NOT detected - running in browser mode');
  1673→        }
  1674→
  1675→        // Test 0.5: Basic ExtendScript test (doesn't require hostScript.jsx)
  1676→        addDiagnosticLog('Test 0.5: Testing basic ExtendScript...');
  1677→        try {
  1678→            const basicResult = await jsx.evalScript('1 + 1');
  1679→            addDiagnosticLog('✓ Basic ExtendScript works: 1+1 = ' + basicResult);
  1680→        } catch (e) {
  1681→            addDiagnosticLog('✗ Basic ExtendScript FAILED: ' + e.message);
  1682→            addDiagnosticLog('  → This means Premiere Pro cannot run ANY ExtendScript');
  1683→            addDiagnosticLog('  → Make sure CEP debug mode is enabled and Premiere was restarted');
  1684→        }
  1685→
  1686→        // Test 0.6: Check if hostScript.jsx functions are loaded
  1687→        addDiagnosticLog('Test 0.6: Checking if hostScript.jsx is loaded...');
  1688→        try {
  1689→            const typeofGetVersion = await jsx.evalScript('typeof getVersion');
  1690→            if (typeofGetVersion === 'function') {
  1691→                addDiagnosticLog('✓ hostScript.jsx is loaded (getVersion exists)');
  1692→            } else {
  1693→                addDiagnosticLog('✗ hostScript.jsx NOT loaded (getVersion type: ' + typeofGetVersion + ')');
  1694→                addDiagnosticLog('  → The JSX file may not be in the correct location');
  1695→            }
  1696→        } catch (e) {
  1697→            addDiagnosticLog('✗ hostScript check FAILED: ' + e.message);
  1698→        }
  1699→
  1700→        // Test 1: JSX Connection
  1701→        addDiagnosticLog('Test 1: Checking JSX connection...');
  1702→        try {
  1703→            const version = await jsx.call('getVersion');
  1704→            addDiagnosticLog('✓ JSX connection OK, version: ' + version);
  1705→        } catch (e) {
  1706→            addDiagnosticLog('✗ JSX connection FAILED: ' + e.message);
  1707→        }
  1708→
  1709→        // Test 2: Check for active sequence
  1710→        addDiagnosticLog('Test 2: Checking for active sequence...');
  1711→        try {
  1712→            const hasSequence = await checkSequenceOpen(0, 0); // No retries for diagnostics
  1713→            addDiagnosticLog('✓ checkSequenceOpen() returned: ' + hasSequence);
  1714→        } catch (e) {
  1715→            addDiagnosticLog('✗ checkSequenceOpen() FAILED: ' + e.message);
  1716→        }
  1717→
  1718→        // Test 3: Get sequence info
  1719→        addDiagnosticLog('Test 3: Getting sequence info...');
  1720→        try {
  1721→            const seqInfo = await jsx.call('getActiveSequence');
  1722→            if (seqInfo && !seqInfo.error) {
  1723→                addDiagnosticLog('✓ Sequence found: ' + (seqInfo.name || 'Unknown'));
  1724→                addDiagnosticLog('  - Sequence ID: ' + (seqInfo.sequenceID || 'N/A'));
  1725→                addDiagnosticLog('  - Duration: ' + (seqInfo.duration || 'N/A'));
  1726→                addDiagnosticLog('  - Frame size: ' + (seqInfo.frameSizeHorizontal || 'N/A') + 'x' + (seqInfo.frameSizeVertical || 'N/A'));
  1727→            } else {
  1728→                addDiagnosticLog('✗ No active sequence found');
  1729→            }
  1730→        } catch (e) {
  1731→            addDiagnosticLog('✗ getActiveSequence() FAILED: ' + e.message);
  1732→        }
  1733→
  1734→        // Test 4: Check project sequences
  1735→        addDiagnosticLog('Test 4: Checking all project sequences...');
  1736→        try {
  1737→            const sequences = await jsx.call('getAllSequences');
  1738→            if (sequences && sequences.length > 0) {
  1739→                addDiagnosticLog('✓ Total sequences in project: ' + sequences.length);
  1740→                sequences.forEach((seq, i) => {
  1741→                    addDiagnosticLog('  - Sequence ' + i + ': ' + (seq.name || 'Unnamed'));
  1742→                });
  1743→            } else {
  1744→                addDiagnosticLog('✗ No sequences found in project');
  1745→            }
  1746→        } catch (e) {
  1747→            addDiagnosticLog('✗ getAllSequences() FAILED: ' + e.message);
  1748→        }
  1749→
  1750→        // Test 5: Check for active sequence via DOM
  1751→        addDiagnosticLog('Test 5: Checking app.project.activeSequence...');
  1752→        try {
  1753→            const result = await jsx.evalScript(`
  1754→                if (app && app.project && app.project.activeSequence) {
  1755→                    JSON.stringify({
  1756→                        success: true,
  1757→                        name: app.project.activeSequence.name,
  1758→                        id: app.project.activeSequence.sequenceID
  1759→                    });
  1760→                } else {
  1761→                    JSON.stringify({
  1762→                        success: false,
  1763→                        reason: app ? (app.project ? 'app.project.activeSequence is null' : 'app.project is null') : 'app is null'
  1764→                    });
  1765→                }
  1766→            `);
  1767→            const data = JSON.parse(result);
  1768→            if (data.success) {
  1769→                addDiagnosticLog('✓ app.project.activeSequence found: ' + data.name + ' (ID: ' + data.id + ')');
  1770→            } else {
  1771→                addDiagnosticLog('✗ app.project.activeSequence NOT found: ' + data.reason);
  1772→            }
  1773→        } catch (e) {
  1774→            addDiagnosticLog('✗ DOM check FAILED: ' + e.message);
  1775→        }
  1776→
  1777→        // Test 6: Check QE DOM availability
  1778→        addDiagnosticLog('Test 6: Checking QE DOM availability...');
  1779→        try {
  1780→            const qeResult = await jsx.evalScript(`
  1781→                if (typeof qe !== 'undefined' && qe !== null) {
  1782→                    JSON.stringify({ available: true });
  1783→                } else {
  1784→                    JSON.stringify({ available: false });
  1785→                }
  1786→            `);
  1787→            const qeData = JSON.parse(qeResult);
  1788→            if (qeData.available) {
  1789→                addDiagnosticLog('✓ QE DOM is available');
  1790→            } else {
  1791→                addDiagnosticLog('✗ QE DOM is NOT available');
  1792→            }
  1793→        } catch (e) {
  1794→            addDiagnosticLog('✗ QE DOM check FAILED: ' + e.message);
  1795→        }
  1796→
  1797→        // Display quick diagnosis
  1798→        updateQuickDiagnosis();
  1799→
  1800→        // Display all logs
  1801→        displayDiagnosticLogs();
  1802→
  1803→    } catch (error) {
  1804→        addDiagnosticLog('DIAGNOSTICS ERROR: ' + error.message);
  1805→        displayDiagnosticLogs();
  1806→    }
  1807→}
  1808→
  1809→function addDiagnosticLog(message) {
  1810→    diagnosticLogs.push(message);
  1811→    console.log('[SPLICE DEBUG] ' + message);
  1812→}
  1813→
  1814→function displayDiagnosticLogs() {
  1815→    if (!ui.debugLogs) return;
  1816→
  1817→    const html = diagnosticLogs.map(log => {
  1818→        return `<div class="debug-log-entry">${escapeHtml(log)}</div>`;
  1819→    }).join('');
  1820→
  1821→    ui.debugLogs.innerHTML = html;
  1822→}
  1823→
  1824→function updateQuickDiagnosis() {
  1825→    if (!ui.debugQuickDiagnosis) return;
  1826→
  1827→    const logs = diagnosticLogs.join(' ');
  1828→    let diagnosis = '';
  1829→    let solution = '';
  1830→
  1831→    // Analyze logs for common issues
  1832→    if (logs.includes('JSX connection FAILED')) {
  1833→        diagnosis = '❌ JSX Connection Failed';
  1834→        solution = 'The panel cannot communicate with Premiere Pro. Try: 1) Close and reopen the panel, 2) Restart Premiere Pro, 3) Check if plugin is properly installed.';
  1835→    } else if (logs.includes('checkSequenceOpen() returned: false') && logs.includes('No active sequence found')) {
  1836→        diagnosis = '❌ No Active Sequence';
  1837→        solution = 'No sequence is active in the Timeline. Double-click a sequence in the Project panel to open it in the Timeline.';
  1838→    } else if (logs.includes('app.project.activeSequence NOT found') && logs.includes('Sequence found')) {
  1839→        diagnosis = '❌ Sequence Not Active';
  1840→        solution = 'A sequence exists but is not the active sequence. Click on the sequence tab in the Timeline to make it active.';
  1841→    } else if (logs.includes('checkSequenceOpen() returned: true') && logs.includes('app.project.activeSequence found')) {
  1842→        diagnosis = '✅ Everything Looks Good';
  1843→        solution = 'The plugin is working correctly. The sequence is detected and active. Try pressing GO again.';
  1844→    } else if (logs.includes('No sequences found in project')) {
  1845→        diagnosis = '❌ No Sequences in Project';
  1846→        solution = 'Create a sequence in Premiere Pro first (File > New > Sequence), then open it in the Timeline.';
  1847→    } else {
  1848→        diagnosis = '⚠️ Unknown Issue';
  1849→        solution = 'Copy the diagnostic logs below and send them to support for further assistance.';
  1850→    }
  1851→
  1852→    ui.debugQuickDiagnosis.innerHTML = `
  1853→        <div class="diagnosis-result">
  1854→            <div class="diagnosis-title">${diagnosis}</div>
  1855→            <div class="diagnosis-solution">${solution}</div>
  1856→        </div>
  1857→    `;
  1858→}
  1859→
  1860→function copyDiagnosticsToClipboard() {
  1861→    const text = diagnosticLogs.join('\n');
  1862→    navigator.clipboard.writeText(text).then(() => {
  1863→        if (ui.copyDiagnosticsBtn) {
  1864→            ui.copyDiagnosticsBtn.textContent = 'Copied!';
  1865→            setTimeout(() => {
  1866→                ui.copyDiagnosticsBtn.textContent = 'Copy to Clipboard';
  1867→            }, 2000);
  1868→        }
  1869→    }).catch(err => {
  1870→        setStatus('Failed to copy: ' + err.message, true);
  1871→    });
  1872→}
  1873→
  1874→// Export for debugging
  1875→window.spliceDebug = {
  1876→    getState: () => ({
  1877→        silences: previewSilences,
