   238â†’// ============================================================================
   239â†’// TEXT EDITOR TOGGLE
   240â†’// ============================================================================
   241â†’function initTextEditorToggle() {
   242â†’    const toggle = document.getElementById('textEditorToggle');
   243â†’    const panel = document.getElementById('text-editor-panel');
   244â†’
   245â†’    if (toggle && panel) {
   246â†’        toggle.addEventListener('click', () => {
   247â†’            const isCollapsed = panel.classList.toggle('collapsed');
   248â†’            const icon = toggle.querySelector('.toggle-icon');
   249â†’            if (icon) {
   250â†’                icon.textContent = isCollapsed ? '+' : '-';
   251â†’            }
   252â†’        });
   253â†’    }
   254â†’}
   255â†’
   256â†’// ============================================================================
   257â†’// SOCIAL REFRAME TOGGLE
   258â†’// ============================================================================
   259â†’function initSocialReframeToggle() {
   260â†’    const toggle = document.getElementById('socialReframeToggle');
   261â†’    const panel = document.getElementById('social-reframe-panel');
   262â†’
   263â†’    if (toggle && panel) {
   264â†’        toggle.addEventListener('click', () => {
   265â†’            const isCollapsed = panel.classList.toggle('collapsed');
   266â†’            const icon = toggle.querySelector('.toggle-icon');
   267â†’            if (icon) {
   268â†’                icon.textContent = isCollapsed ? '+' : '-';
   269â†’            }
   270â†’        });
