   292â†’// ============================================================================
   293â†’// SLIDERS
   294â†’// ============================================================================
   295â†’function initSliders() {
   296â†’    // Sensitivity slider
   297â†’    if (ui.sensitivitySlider && ui.sensitivityValue) {
   298â†’        ui.sensitivitySlider.addEventListener('input', () => {
   299â†’            ui.sensitivityValue.textContent = ui.sensitivitySlider.value;
   300â†’            saveSettings({ sensitivity: parseInt(ui.sensitivitySlider.value) });
   301â†’        });
   302â†’    }
   303â†’
   304â†’    // J-Cut sliders
   305â†’    if (ui.jcutLeadIn && ui.jcutLeadInValue) {
   306â†’        ui.jcutLeadIn.addEventListener('input', () => {
   307â†’            const val = (ui.jcutLeadIn.value / 100).toFixed(2);
   308â†’            ui.jcutLeadInValue.textContent = val + 's';
   309â†’            saveSettings({ jcutLeadIn: parseFloat(val) });
   310â†’        });
   311â†’    }
   312â†’
   313â†’    if (ui.jcutLeadOut && ui.jcutLeadOutValue) {
   314â†’        ui.jcutLeadOut.addEventListener('input', () => {
   315â†’            const val = (ui.jcutLeadOut.value / 100).toFixed(2);
   316â†’            ui.jcutLeadOutValue.textContent = val + 's';
   317â†’            saveSettings({ jcutLeadOut: parseFloat(val) });
   318â†’        });
   319â†’    }
   320â†’}
   321â†’
   322â†’// ============================================================================
   323â†’// FEATURE TOGGLES
   324â†’// ============================================================================
   325â†’function initFeatureToggles() {
   326â†’    // J-Cut toggle
   327â†’    if (ui.enableJCut && ui.jcutSettings) {
   328â†’        ui.enableJCut.addEventListener('change', () => {
   329â†’            ui.jcutSettings.classList.toggle('collapsed', !ui.enableJCut.checked);
   330â†’            saveSettings({ enableJCut: ui.enableJCut.checked });
   331â†’        });
   332â†’    }
   333â†’
   334â†’    // Zoom toggle
   335â†’    if (ui.enableZoom && ui.zoomSettings) {
   336â†’        ui.enableZoom.addEventListener('change', () => {
   337â†’            ui.zoomSettings.classList.toggle('collapsed', !ui.enableZoom.checked);
   338â†’            saveSettings({ enableZoom: ui.enableZoom.checked });
   339â†’        });
   340â†’    }
   341â†’
   342â†’    // Chapters toggle
   343â†’    if (ui.enableChapters && ui.chapterSettings) {
   344â†’        ui.enableChapters.addEventListener('change', () => {
   345â†’            ui.chapterSettings.classList.toggle('collapsed', !ui.enableChapters.checked);
   346â†’            saveSettings({ enableChapters: ui.enableChapters.checked });
   347â†’        });
   348â†’    }
   349â†’
   350â†’    // Takes detection toggle
   351â†’    if (ui.enableTakesDetection) {
   352â†’        ui.enableTakesDetection.addEventListener('change', () => {
   353â†’            saveSettings({ enableTakesDetection: ui.enableTakesDetection.checked });
   354â†’        });
   355â†’    }
   356â†’
   357â†’    // Profanity detection toggle
   358â†’    if (ui.enableProfanity && ui.profanitySettings) {
   359â†’        ui.enableProfanity.addEventListener('change', () => {
   360â†’            ui.profanitySettings.classList.toggle('collapsed', !ui.enableProfanity.checked);
   361â†’            saveSettings({ enableProfanity: ui.enableProfanity.checked });
   362â†’        });
   363â†’    }
   364â†’
   365â†’    // Filler word detection toggle
   366â†’    if (ui.enableFillerDetection && ui.fillerSettings) {
   367â†’        ui.enableFillerDetection.addEventListener('change', () => {
   368â†’            ui.fillerSettings.classList.toggle('collapsed', !ui.enableFillerDetection.checked);
   369â†’            saveSettings({ enableFillerDetection: ui.enableFillerDetection.checked });
   370â†’        });
   371â†’    }
   372â†’}
   373â†’
   374â†’// ============================================================================
   375â†’// PRESET SELECTOR
   376â†’// ============================================================================
   377â†’// PRESETS is defined in config.js
   378â†’
   379â†’function initPresetSelector() {
   380â†’    if (!ui.presetSelector) return;
   381â†’
   382â†’    ui.presetSelector.addEventListener('change', () => {
   383â†’        const presetId = ui.presetSelector.value;
   384â†’        applyPreset(presetId);
   385â†’    });
   386â†’
   387â†’    // Load current preset
   388â†’    const settings = getSettings();
   389â†’    if (settings.activePreset) {
   390â†’        ui.presetSelector.value = settings.activePreset;
   391â†’    }
   392â†’}
   393â†’
   394â†’function applyPreset(presetId) {
   395â†’    const preset = PRESETS[presetId];
   396â†’    if (!preset) return;
   397â†’
   398â†’    // Custom preset - keep current settings
   399â†’    if (presetId === 'custom' || !preset.settings) {
   400â†’        saveSettings({ activePreset: 'custom' });
   401â†’        setStatus('Using custom settings');
   402â†’        return;
   403â†’    }
   404â†’
   405â†’    const settings = preset.settings;
   406â†’
   407â†’    // Apply to UI
   408â†’    if (ui.sensitivitySlider && settings.sensitivity !== undefined) {
   409â†’        ui.sensitivitySlider.value = settings.sensitivity;
   410â†’        if (ui.sensitivityValue) ui.sensitivityValue.textContent = settings.sensitivity;
   411â†’    }
   412â†’
   413â†’    if (ui.enableJCut) {
   414â†’        ui.enableJCut.checked = settings.enableJCut || false;
   415â†’        if (ui.jcutSettings) {
   416â†’            ui.jcutSettings.classList.toggle('collapsed', !settings.enableJCut);
   417â†’        }
   418â†’    }
   419â†’
   420â†’    if (settings.jCutLeadIn !== undefined && ui.jcutLeadIn) {
   421â†’        ui.jcutLeadIn.value = Math.round(settings.jCutLeadIn * 100);
   422â†’        if (ui.jcutLeadInValue) ui.jcutLeadInValue.textContent = settings.jCutLeadIn + 's';
   423â†’    }
   424â†’
   425â†’    if (settings.jCutLeadOut !== undefined && ui.jcutLeadOut) {
   426â†’        ui.jcutLeadOut.value = Math.round(settings.jCutLeadOut * 100);
   427â†’        if (ui.jcutLeadOutValue) ui.jcutLeadOutValue.textContent = settings.jCutLeadOut + 's';
   428â†’    }
   429â†’
   430â†’    // Save merged settings
   431â†’    saveSettings({
   432â†’        activePreset: presetId,
   433â†’        sensitivity: settings.sensitivity,
   434â†’        threshold: settings.threshold,
   435â†’        minSilenceLength: settings.minSilenceLength,
   436â†’        paddingStart: settings.paddingStart,
   437â†’        paddingEnd: settings.paddingEnd,
   438â†’        enableTakesDetection: settings.enableTakesDetection,
   439â†’        enableJCut: settings.enableJCut || false,
   440â†’        jcutLeadIn: settings.jCutLeadIn || 0.3,
   441â†’        jcutLeadOut: settings.jCutLeadOut || 0.2
   442â†’    });
   443â†’
   444â†’    setStatus(`Applied ${preset.name} preset`);
   445â†’}
   446â†’
   447â†’// ============================================================================
   448â†’// LOAD SETTINGS TO UI
   449â†’// ============================================================================
   450â†’function loadSettingsToUI() {
   451â†’    const settings = getSettings();
   452â†’
   453â†’    if (ui.sensitivitySlider) {
   454â†’        ui.sensitivitySlider.value = settings.sensitivity;
   455â†’        if (ui.sensitivityValue) ui.sensitivityValue.textContent = settings.sensitivity;
   456â†’    }
   457â†’
   458â†’    if (ui.enableTakesDetection) ui.enableTakesDetection.checked = settings.enableTakesDetection;
   459â†’    if (ui.enableJCut) ui.enableJCut.checked = settings.enableJCut;
   460â†’    if (ui.enableZoom) ui.enableZoom.checked = settings.enableZoom;
   461â†’    if (ui.enableChapters) ui.enableChapters.checked = settings.enableChapters;
   462â†’
   463â†’    if (ui.jcutSettings) ui.jcutSettings.classList.toggle('collapsed', !settings.enableJCut);
   464â†’    if (ui.zoomSettings) ui.zoomSettings.classList.toggle('collapsed', !settings.enableZoom);
   465â†’    if (ui.chapterSettings) ui.chapterSettings.classList.toggle('collapsed', !settings.enableChapters);
   466â†’
   467â†’    if (ui.jcutLeadIn) {
   468â†’        ui.jcutLeadIn.value = Math.round(settings.jcutLeadIn * 100);
   469â†’        if (ui.jcutLeadInValue) ui.jcutLeadInValue.textContent = settings.jcutLeadIn + 's';
   470â†’    }
   471â†’    if (ui.jcutLeadOut) {
   472â†’        ui.jcutLeadOut.value = Math.round(settings.jcutLeadOut * 100);
   473â†’        if (ui.jcutLeadOutValue) ui.jcutLeadOutValue.textContent = settings.jcutLeadOut + 's';
   474â†’    }
   475â†’
   476â†’    if (ui.presetSelector && settings.activePreset) {
   477â†’        ui.presetSelector.value = settings.activePreset;
   478â†’    }
   479â†’}
