   940â†’async function buildSequence() {
   941â†’    if (isOperationInProgress) return;
   942â†’
   943â†’    isOperationInProgress = true;
   944â†’    setStatus('Building sequence...');
   945â†’
   946â†’    try {
   947â†’        const settings = getSettings();
   948â†’
   949â†’        // Ensure we have sequence info
   950â†’        if (!currentSequenceInfo) {
   951â†’            currentSequenceInfo = await jsx.call('getActiveSequence');
   952â†’        }
   953â†’
   954â†’        // Create cut list from selected silences
   955â†’        const selectedSilences = previewSilences.filter((_, i) => selectedSilenceIndices.has(i));
   956â†’
   957â†’        // Calculate total duration from sequence info or last silence
   958â†’        let duration = 0;
   959â†’        if (currentSequenceInfo && currentSequenceInfo.duration) {
   960â†’            duration = currentSequenceInfo.duration;
   961â†’        } else if (previewSilences.length > 0) {
   962â†’            const lastSilence = previewSilences[previewSilences.length - 1];
   963â†’            duration = lastSilence.end + 10; // Add buffer
   964â†’        }
   965â†’
   966â†’        // Call backend to generate cut list
   967â†’        const cutListResponse = await fetchWithTimeout(
   968â†’            `${getBackendUrl()}/cut-list`,
   969â†’            {
   970â†’                method: 'POST',
   971â†’                headers: getAuthHeaders(),
   972â†’                body: JSON.stringify({
   973â†’                    sourceName: currentSequenceInfo?.name || 'Untitled Sequence',
   974â†’                    sourcePath: currentSequenceInfo?.treePath || currentSequenceInfo?.name || 'Untitled',
   975â†’                    duration: duration,
   976â†’                    silences: selectedSilences,
   977â†’                    takes: previewTakes,
   978â†’                    enableTakes: settings.enableTakesDetection,
   979â†’                    jCutOffset: settings.enableJCut ? settings.jcutLeadIn : 0,
   980â†’                    lCutOffset: settings.enableJCut ? settings.jcutLeadOut : 0
   981â†’                })
   982â†’            }
   983â†’        );
   984â†’
   985â†’        if (!cutListResponse.ok) {
   986â†’            throw new Error(await parseErrorResponse(cutListResponse));
   987â†’        }
   988â†’
   989â†’        const cutList = await cutListResponse.json();
   990â†’
   991â†’        // Build sequence via JSX
   992â†’        const result = await jsx.call('buildSequenceFromCutList', JSON.stringify(cutList));
   993â†’
   994â†’        if (result && result.success) {
   995â†’            setStatus(`Built sequence: ${result.sequenceName}`);
   996â†’            hidePreview();
   997â†’        } else {
   998â†’            throw new Error(result?.error || 'Build failed');
   999â†’        }
  1000â†’    } catch (error) {
  1001â†’        setStatus('Build failed: ' + error.message, true);
  1002â†’    } finally {
  1003â†’        isOperationInProgress = false;
  1004â†’    }
  1005â†’}
