   332â†’/**
   333â†’ * SECURITY: Escape HTML to prevent XSS attacks
   334â†’ * Use this when inserting untrusted content into innerHTML
   335â†’ * @param {string} str - String to escape
   336â†’ * @returns {string} Escaped string safe for innerHTML
   337â†’ */
   338â†’function escapeHtml(str) {
   339â†’    if (str === null || str === undefined) return '';
   340â†’    if (typeof str !== 'string') str = String(str);
   341â†’
   342â†’    const escapeMap = {
   343â†’        '&': '&amp;',
   344â†’        '<': '&lt;',
   345â†’        '>': '&gt;',
   346â†’        '"': '&quot;',
   347â†’        "'": '&#x27;',
   348â†’        '/': '&#x2F;',
   349â†’        '`': '&#x60;'
   350â†’    };
   351â†’
   352â†’    return str.replace(/[&<>"'\/`]/g, char => escapeMap[char]);
   353â†’}
   354â†’
   355â†’/**
   356â†’ * SECURITY: Create safe HTML from template with escaped values
   357â†’ * @param {TemplateStringsArray} strings - Template strings
   358â†’ * @param {...any} values - Values to escape
   359â†’ * @returns {string} HTML with escaped values
   360â†’ */
   361â†’function safeHtml(strings, ...values) {
   362â†’    return strings.reduce((result, str, i) => {
   363â†’        const value = values[i - 1];
   364â†’        const escaped = escapeHtml(value);
   365â†’        return result + escaped + str;
   366â†’    });
   367â†’}
   368â†’
   369â†’/**
   370â†’ * Format time in MM:SS or HH:MM:SS
   371â†’ */
   372â†’function formatTime(seconds) {
   373â†’    if (isNaN(seconds) || seconds < 0) return '0:00';
   374â†’
   375â†’    const hours = Math.floor(seconds / 3600);
   376â†’    const minutes = Math.floor((seconds % 3600) / 60);
   377â†’    const secs = Math.floor(seconds % 60);
   378â†’
   379â†’    if (hours > 0) {
   380â†’        return `${hours}:${minutes.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
   381â†’    }
   382â†’    return `${minutes}:${secs.toString().padStart(2, '0')}`;
   383â†’}
