   396â†’/**
   397â†’ * Debounce function calls
   398â†’ */
   399â†’function debounce(func, wait) {
   400â†’    let timeout;
   401â†’    return function executedFunction(...args) {
   402â†’        const later = () => {
   403â†’            clearTimeout(timeout);
   404â†’            func(...args);
   405â†’        };
   406â†’        clearTimeout(timeout);
   407â†’        timeout = setTimeout(later, wait);
   408â†’    };
   409â†’}
