    if (ui.optionsToggle && ui.optionsPanel) {
        ui.optionsToggle.addEventListener('click', () => {
            const isExpanded = ui.optionsPanel.classList.toggle('collapsed');
            ui.optionsToggle.classList.toggle('expanded', !isExpanded);
            saveSettings({ expandedOptions: !isExpanded });
        });

        // Restore state
