    13→    await test_45_save_state('Before multitrack audio');
    14→    await test_46_retrieve_undo_state_info();
    15→
    16→    const trackingId = tracker['start']('multitrack_audio');
    17→
    18→    try {
    19→        showSpinner('Detecting silences for each track');
    20→        let multitrack_parameters = await test_48_get_multitrack_params();
    21→
    22→        if (!multitrack_parameters['scopeValid']) {
    23→            showError('Please ensure your In/Out points are valid');
    24→            hideSpinner();
    25→            return;
    26→        }
    27→
    28→        // Determine method for removing silences (local processing vs JSX script)
    29→        const method = (multitrack_parameters['method'] == 'original') ? 'jsx_and_compare' : 'local';
    30→        const timeMethod = (multitrack_parameters['method'] == 'original') ? 'Seconds' : 'Frames';
    31→
    32→        // Loop through selected audio tracks
    33→        const audioTrackIndices = multitrack_parameters['keyAudioTrackIndices'];
    34→        for (let trackLoopIndex = 0; trackLoopIndex < audioTrackIndices['length']; trackLoopIndex++) {
    35→            const trackIndex = audioTrackIndices[trackLoopIndex];
    36→            const inPoint = multitrack_parameters['inPoint'];
    37→            const outPoint = multitrack_parameters['outPoint'];
    38→
    39→            // Chunk duration constant (likely frames). 1800 frames @ 60fps = 30s, @ 30fps = 60s.
    40→            const chunkDuration = 1800;
