/**
 * Main logic for transcribing audio and removing/muting profanity.
 * This file handles the workflow of:
 * 1. Validating licenses.
 * 2. Transcribing audio in chunks to avoid timeouts or limits.
 * 3. Processing the transcription to identify profanity.
 * 4. Updating the UI with the results.
 * 5. Muting or bleeping identified profanity in the timeline.
 */

/**
 * Transcribes the audio track to detect profanity suitable for removal.
 * Allows overriding parameters for testing or batch processing.
 *
 * @param {Object|null} overrideParams - Optional parameters to override default settings (e.g. inPoint, outPoint).
 * @param {boolean} backgroundMode - If true, suppresses UI updates like menus and spinners.
 * @returns {Promise<Object|void>} - Returns status object in background mode, otherwise void.
 */
async function transcribe_for_remove_profanity(overrideParams = null, backgroundMode = false) {
    const licenseValid = await verify_license_throttled();
    if (!licenseValid || !check_function_availability('remove_profanity')) {
        showError('Please provide a valid license key in the Settings menu');
        return;
    }

    // Schedule log upload and save current state before starting
    test_52_schedule_log_upload(time_delay = 0);
    regular_license_key_checker();
    await test_45_save_state('Before transcribing for captions');
    await test_46_retrieve_undo_state_info();

    const trackerId = tracker['start']('transcribe_for_remove_profanity');

    try {
        // Get parameters for transcription (in/out points, language, etc.)
        let params = overrideParams || await test_94_get_profanity_transcribe_params();
        console.log('profanity_transcribe_params', params);
        g_transcript_captions_original_inPoint_used = params['inPoint'];

        if (!params['scopeValid']) {
            showError('Please select a valid scope (you may need to set the in and out points explicitly)');
            hideSpinner();
            return;
        }

        // Divide the timeline into smaller chunks to process
        const chunks = [];
        let currentInPoint = params['inPoint'];
        while (true) {
            let currentOutPoint = Math.min(currentInPoint + captions_max_segment_length, params['outPoint']);

            // Adjust segment length to avoid very short segments at the end
            if (currentInPoint + captions_max_segment_length + captions_min_segment_length > params['outPoint']) {
                currentOutPoint = params['outPoint'];
            }

            chunks.push([currentInPoint, currentOutPoint]);
            currentInPoint = currentOutPoint;
            if (currentOutPoint >= params['outPoint']) break;
        }

        showSpinner(spinner_msg_chapters_analysis);
        console.log('Transcribing audio');

        // Process each chunk
        for (let chunkIndex = 0; chunkIndex < chunks.length; chunkIndex++) {
            const chunk = chunks[chunkIndex];
            showSpinner('Transcribing... (' + (chunkIndex + 1) + ' of ' + chunks.length + ')');
            const [chunkStart, chunkEnd] = chunk;

            // setInPoint/setOutPoint might be setting the work area for the transcription engine
            await jsxProject['setInPoint'](chunkStart);
            await jsxProject['setOutPoint'](chunkEnd);

            // Refresh parameters in case they changed or need to be localized to the chunk
            params = overrideParams || await test_94_get_profanity_transcribe_params();
            params['inPoint'] = chunkStart;
            params['outPoint'] = chunkEnd;
            g_transcript_inPoint_used = params['inPoint'];

            let transcriptData = null;
            // Call the actual transcription API
            transcriptData = await test_10_get_transcript(params['inPoint'], params['outPoint'], params['language']);

            // Adjust timestamps to be relative to the timeline info
            for (let segmentIndex = 0; segmentIndex < transcriptData['segments']['length']; segmentIndex++) {
                transcriptData['segments'][segmentIndex]['start'] += params['inPoint'];
                transcriptData['segments'][segmentIndex]['end'] += params['inPoint'];
            }

            // Convert format if necessary
            transcriptData = openai_transcript_to_captions_object(transcriptData, params['language']);

            if (transcriptData === null) throw new Error('Transcription failed');

            // Filter out segments that go beyond the requested outPoint
            // (Using -1 for the last index)
            if (transcriptData['segments'][transcriptData['segments']['length'] - 1]['inPoint'] > params['outPoint']) {
                const filteredSegments = transcriptData['segments']['filter'](segment => segment['inPoint'] <= params['outPoint']);
                console.log('Removed (' + (transcriptData['segments']['length'] - filteredSegments['length']) + ') segments that started after the outPoint');

