function detect_repetition_advanced() {
    // Thresholds and Parameters
    const MAX_OCCURRENCES_TO_FIND = 10;
    const params = test_60_get_advanced_takes_selection_params();
    const similarity_threshold = 1 - params['tolerance'];
    const phrase_size = params['phrase_size'];
    const search_radius = params['search_radius'];
    const extend_mode = params['extend_last']; // 'longest-occurrence' or 'next-punctuation'

    // Highlighter CSS classes (cycling colors)
    const highlight_classes = [
        'text-underline rounded-sm bg-opacity-25 border-b bg-yellow-300   border-yellow-300   text-yellow-300',
        'text-underline rounded-sm bg-opacity-25 border-b bg-emerald-300  border-emerald-300  text-emerald-300',
        'text-underline rounded-sm bg-opacity-25 border-b bg-purple-300   border-purple-300   text-purple-300',
        'text-underline rounded-sm bg-opacity-25 border-b bg-red-300      border-red-300      text-red-300',
        'text-underline rounded-sm bg-opacity-25 border-b bg-orange-300   border-orange-300   text-orange-300',
        'text-underline rounded-sm bg-opacity-25 border-b bg-cyan-300     border-cyan-300     text-cyan-300'
    ];

    const transcript_text = g_transcript['text'];
    const words_raw = transcript_text.split(/\s+/);
    // Normalize words: lowercase and remove punctuation for comparison
    const words_normalized = transcript_text.split(/\s+/).map(s => s.toLowerCase()).map(s => s.replace(/[.,\/#!$%\^&\*;:{}=\-_`~()]/g, ''));

    let phrase_start_index = 0;
    let found_phrases_occurrences = []; // Array of arrays, where each inner array is a list of start indices for that phrase occurrence group
    g_all_phrase_bounds = []; // Global variable to store results

    // 1. Scan the transcript for phrases
    for (phrase_start_index = 0; phrase_start_index < words_normalized.length - phrase_size; phrase_start_index++) {
        let current_phrase_words = words_normalized.slice(phrase_start_index, phrase_start_index + phrase_size);
        let current_phrase_occurrences = [];

        // Look ahead within the search radius
        let search_limit = Math.min(words_normalized.length, phrase_start_index + phrase_size + search_radius);

        for (let l = phrase_start_index; l < search_limit - phrase_size; l++) {
            let comparison_phrase_words = words_normalized.slice(l, l + phrase_size);

            // Check similarity
            if (tools['arrays_similarity'](comparison_phrase_words, current_phrase_words) >= similarity_threshold) {
                if (current_phrase_occurrences.length > 0) {
                    // Log subsequent matches
                    console.log('--- FOUND ---');
                    console.log('phrase', current_phrase_words.join(' '));
                    console.log('matched with');
                    console.log(words_normalized.slice(l, l + phrase_size).join(' '));
                }
                current_phrase_occurrences.push(l);
            }

            if (current_phrase_occurrences.length >= MAX_OCCURRENCES_TO_FIND) break;
        }

        // Filter out if no repetition found (length <= 1 means only found itself)
        if (current_phrase_occurrences.length <= 1) continue;

        // Remove overlapping matches within the same group
        if (current_phrase_occurrences.length > 2) {
            for (let w = 1; w < current_phrase_occurrences.length - 1; w++) {
                // If current occurrence overlaps with the next one
                if (current_phrase_occurrences[w] + phrase_size > current_phrase_occurrences[w + 1]) {
                    current_phrase_occurrences.splice(w, 1);
                    w--;
                }
            }
        }
        if (current_phrase_occurrences.length <= 1) continue;

