    g_profanity_transcript = null;

function set_profanity_transcript(transcript) {
    g_profanity_transcript = transcript;
}

function set_profanity_language(language) {
    g_profanity_language = language;
}

/**
 * Scans captions for profanity and returns a list of found profanity words.
 * 
 * @param {string} customBlocklist - User-defined blocklist string
 * @param {string} customAllowlist - User-defined allowlist string
 * @returns {Array} List of profanity word objects with their global index
 */
function mark_profanity_words(customBlocklist, customAllowlist) {
    const options = {
        'language': g_profanity_language,
        'profanity': true,
        'profanity_mode': 'standard',
        'profanity_custom_blocklist': customBlocklist || '',
        'profanity_custom_allowlist': customAllowlist || ''
    };

