    // NOTE: This map operation creates a new structure with formatted text but 
    // does not assign it to anything. In the original obfuscated code, this 
    // block existed but its result was ignored. It is likely dead code, 
    // unless 'applyFormattingToCaptionWord' has necessary side effects.
    g_captions_chunked.map(chunk => {
        return {
            ...chunk,
            'type': 'caption',
            'words': chunk['words'].map(word => {
                return {
                    ...word,
                    'text': applyFormattingToCaptionWord(word, options, g_profanity_language)
                };
            })
        };
    });

    const profanityWordsList = [];

    // Iterate through all caption chunks and words to identify profanity
    g_captions_chunked.forEach(chunk => {
        chunk['words'].forEach(word => {
            if (word['isProfanity']) {
                // Determine if 'isProfanityChecked' property needs to be initialized
                // This controls whether the checkbox in the UI is checked by default
                if (!Object.keys(word).includes('isProfanityChecked')) {
                    word['isProfanityChecked'] = true;
                }

                profanityWordsList.push({
                    'word': word,
                    'index': globalWordIndex
                });
            }
            globalWordIndex++;
        });
    });

