     6→ * @param {Array<{word: string}>} wordList - The list of words from the transcript.
     7→ * @param {boolean} [isHighlight=true] - Whether to highlight the target word in bold/underline.
     8→ * @returns {string} The constructed HTML string with context.
     9→ */
    10→function write_word_with_context(index, wordList, isHighlight = true) {
    11→    const CONTEXT_RANGE = 3;
    12→    let result = '';
    13→
    14→    // Add prefix ellipsis if not at the start
    15→    if (index === 0) {
    16→        // Do nothing (result starts empty)
    17→    } else {
    18→        result = '... ';
    19→    }
    20→
    21→    const start = index - CONTEXT_RANGE;
    22→    const end = index + CONTEXT_RANGE;
    23→
    24→    for (let i = start; i <= end; i++) {
    25→        // Bounds check
    26→        if (i < 0 || i >= wordList.length) continue;
    27→
    28→        let wordHtml;
    29→        if (isHighlight && i === index) {
    30→            wordHtml = '<b class="text-white not-italic font-bold underline">' + wordList[i].word + '</b>';
    31→        } else {
    32→            wordHtml = wordList[i].word;
    33→        }
    34→
    35→        result += wordHtml + ' ';
    36→    }
    37→
    38→    // Add suffix ellipsis if not at the end
    39→    if (index + CONTEXT_RANGE < wordList.length - 1) {
    40→        result += '...';
    41→    }
    42→
    43→    return result;
    44→}
