/**
 * Generates an HTML string for a word with its surrounding context.
 * Highlights the target word if requested.
 *
 * @param {number} index - The index of the word in the wordList.
 * @param {Array<{word: string}>} wordList - The list of words from the transcript.
 * @param {boolean} [isHighlight=true] - Whether to highlight the target word in bold/underline.
 * @returns {string} The constructed HTML string with context.
 */
function write_word_with_context(index, wordList, isHighlight = true) {
    const CONTEXT_RANGE = 3;
    let result = '';

    // Add prefix ellipsis if not at the start
    if (index === 0) {
        // Do nothing (result starts empty)
    } else {
        result = '... ';
    }

    const start = index - CONTEXT_RANGE;
    const end = index + CONTEXT_RANGE;

    for (let i = start; i <= end; i++) {
        // Bounds check
        if (i < 0 || i >= wordList.length) continue;

        let wordHtml;
        if (isHighlight && i === index) {
            wordHtml = '<b class="text-white not-italic font-bold underline">' + wordList[i].word + '</b>';
        } else {
            wordHtml = wordList[i].word;
        }

        result += wordHtml + ' ';
    }

    // Add suffix ellipsis if not at the end
    if (index + CONTEXT_RANGE < wordList.length - 1) {
        result += '...';
    }

    return result;
}

/**
 * Main function to render the profanity table and transcript view.
 * Sets up the UI with found profane words and attaches event listeners.
 *
 * @param {any} unused - Unused parameter from original obfuscated code.
 * @param {Object} transcriptData - The full transcript object containing words and segments.
 * @param {Array<Object>} profanityList - List of identified profane words with metadata.
 */
async function writeProfanityTableHTML(unused, transcriptData, profanityList) {
    // Calculate frame duration for timecode conversion (1 / frameRate)
    let frameDuration = 1 / await getVideoFrameRate();

    $('#profanity-words-found').text(profanityList.length);
    const tableBody = $('#profanity-table tbody');
    let rowsHtml = '';

    profanityList.forEach((item, idx) => {
        // Convert timestamp to hh:mm:ss format
        let timeStr = timeConversion(item.word.start, 's', 'hh:mm:ss', frameDuration);

        rowsHtml += `
      <tr class="profanity-table-row" profanity-number="${idx}">
        <td>
          <button class="button-profanity-time-section px-1 py-1 flex justify-start w-full m-0 rounded-md text-sm text-white bg-shorts-500 border-shorts-400 hover:bg-shorts-300 hover:border-shorts-200 active:bg-shorts-500 active:border-shorts-400">
            <div class="px-1 w-20 text-left">${timeStr}</div>
            <div class="px-1 text-white italic">${write_word_with_context(item.index, transcriptData.words, true)}</div>
          </button>
        </td>
        <td>
          <div class="checkbox-profanity-include p-2 w-full cursor-pointer">
            <input
              type="checkbox"
              ${item.word.isProfanityChecked ? 'checked="checked"' : ''}
              class="accent-purple-700 pointer-events-none rounded-md w-full px-1" />
          </div>

