/**
 * Extracts audio from the active sequence by rendering it to a file.
 * @param {string} [scope='entire'] - The scope of the audio extraction ('entire', 'inout', etc.).
 * @param {string} [format='mp3'] - The format to render the audio to ('mp3', 'wav').
 * @returns {Promise<string>} The file path of the extracted audio file.
 */
async function extractAudioFromSequence(scope = 'entire', format = 'mp3') {
    console.log('extractAudioFromSequence called with scope = ', scope);

    // Define the Premiere Pro sequence to be rendered.
    const sequencePath = 'app.project.activeSequence';

    // Render the audio from the specified sequence, scope, and format.
    const filePath = await render(sequencePath, format, scope);
    console.log(filePath);

    // Update the UI with the path to the rendered file.
    $('#path').val(filePath);

    return filePath;
}

/**
 * Detects silences in an audio file and displays a plot of the waveform and detected silences.
 * @param {string} [filePath=null] - The path to the audio file. If null, it's read from the '#path' input field.
 * @returns {Promise<Array<Array<number>>|null>} An array of silent ranges [start, end] in seconds, or null if no silences are found.
 */
async function runSilenceDetectionWorkflow(filePath = null) {
    if (filePath == null) {
        filePath = $('#path').val();
    }
    const audioData = await getAudioFloatArray(filePath);
    let channelData = audioData.channelData,
        sampleRate = audioData.sampleRate;
    const totalSamples = channelData[0].length;

    // Prepare data for plotting the main waveform.
    const waveformPlotData = {
        x: divideArrayByScalar(Array.from({
            length: totalSamples
        }, (v, i) => i), sampleRate),
        y: channelData[0]
    };

    console.log('sampleRate', sampleRate);
    let eval_string = 'getVideoFrameRateInSeconds()';
    console.log('eval_string', eval_string);
    let videoFrameRate = await new Promise((resolve, reject) => {
        jsx.evalScript(eval_string, function(result) {
            console.log('result', result);
            resolve(result);
        });
    });
    videoFrameRate = parseFloat(videoFrameRate);
    console.log('videoFrameRate', videoFrameRate);

    const silenceCuttingAggressiveness = parseFloat($('#range-silence-cutting').val());
    // `silence_cutting_parameters_stops` is a global or external variable defining parameter stops for aggressiveness.
    var silenceParams = getInterpolatedValueFromStops(silenceCuttingAggressiveness, silence_cutting_parameters_stops);
    console.log('silence_cutting_parameters before rounding', JSON.parse(JSON.stringify(silenceParams)));

    // Align silence detection parameters to the video frame rate to ensure cuts happen on frame boundaries.
    silenceParams.min_silence_length = videoFrameRate * Math.round(silenceParams.min_silence_length / videoFrameRate);
    silenceParams.seek_step = videoFrameRate * Math.round(silenceParams.seek_step / videoFrameRate);
    silenceParams.min_silence_length = Math.max(silenceParams.min_silence_length, videoFrameRate);
    silenceParams.seek_step = Math.max(silenceParams.seek_step, videoFrameRate);
    console.log('silence_cutting_parameters after rounding', JSON.parse(JSON.stringify(silenceParams)));

    const {
        silentRanges: silences,
        RMS: rmsData
    } = detectSilence(channelData[0], (minSilenceLen = silenceParams.min_silence_length), (silenceThresh = silenceParams.threshold), (seekStep = silenceParams.seek_step), sampleRate);
    console.log('silences', silences);

    if (!silences) return null;

    // Align detected silences to frame boundaries.
    silences = silences.map(range => [videoFrameRate * Math.round(parseInt(range[0]) / videoFrameRate), videoFrameRate * Math.round(parseInt(range[1]) / videoFrameRate)]);
    console.log(silences);

