async function turboSilenceCutting(cuts) {
    const trackerId = tracker.start('new_silence_cutting_turbo');
    const reversedCuts = JSON.parse(JSON.stringify(cuts)).reverse();
    let [xmlRoot, nextObjectId] = await prepareEnvironmentCaptionsTurbo();
    const sequenceID = await jsxExecute('app.project.activeSequence.sequenceID');
    const sequenceNode = xmlRoot.find('Sequence[ObjectUID="' + sequenceID + '"]');
    console.log('Sequence[ObjectUID="' + sequenceID + '"]');
    const trackGroupRefs = sequenceNode.find('TrackGroups > TrackGroup > Second');
    console.log('TrackGroups > TrackGroup > Second, ' + trackGroupRefs.length);
    const videoTrackGroup = xmlRoot.find('VideoTrackGroup[ObjectID="' + $(trackGroupRefs[0]).attr('ObjectRef') + '"]');
    console.log('VideoTrackGroup[ObjectID="' + $(trackGroupRefs[0]).attr('ObjectRef') + '"]');
    const audioTrackGroup = xmlRoot.find('AudioTrackGroup[ObjectID="' + $(trackGroupRefs[1]).attr('ObjectRef') + '"]');
    console.log('AudioTrackGroup[ObjectID="' + $(trackGroupRefs[1]).attr('ObjectRef') + '"]');
    const links = sequenceNode.find('PersistentGroupContainer > LinkContainer > Links > Link').map((i, el) => $(el).attr('ObjectRef'));
    console.log('PersistentGroupContainer > LinkContainer > Links > Link, ' + links.length);
    const linkGroups = [];
    links.each((i, linkRef) => {
        const group = [];
        xmlRoot.find('Link[ObjectID="' + linkRef + '"] > TrackItemGroup > TrackItems > TrackItem').each((j, item) => {
            group.push($(item).attr('ObjectRef'));
        });
        linkGroups.push(group);
    });
    console.log('Found ' + linkGroups.length + ' links');
    const clipTrackItems = [];
    getClipTrackItemsFromGroup(xmlRoot, videoTrackGroup, 'Video').forEach(item => clipTrackItems.push(item));
