     1→/**
     2→ * SPLICE Multitrack UI Module
     3→ *
     4→ * Connects the multitrackAnalysis.js backend to the user interface.
     5→ * Handles speaker configuration, track mapping, analysis, and applying cuts.
     6→ *
     7→ * Features:
     8→ * - Dynamic speaker configuration (add/remove)
     9→ * - Video track mapping per speaker
    10→ * - Parameter controls (min shot, wide shot %, switching frequency)
    11→ * - Distribution chart visualization
    12→ * - Decision list with seek-to-time
    13→ */
    14→
    15→// ============================================================================
    16→// STATE
    17→// ============================================================================
    18→let speakerConfig = [];
    19→let analysisResults = null;
    20→let isMultitrackOperationInProgress = false;
    21→
    22→// Speaker colors for visual distinction
    23→const SPEAKER_COLORS = ['#ff6b6b', '#4ecdc4', '#ffe66d', '#95e1d3'];
    24→
    25→// ============================================================================
    26→// DOM ELEMENT CACHE
    27→// ============================================================================
    28→const multitrackUI = {};
    29→
    30→function cacheMultitrackElements() {
    31→  multitrackUI.section = document.getElementById('multitrackSection');
    32→  multitrackUI.toggle = document.getElementById('multitrackToggle');
    33→  multitrackUI.panel = document.getElementById('multitrackPanel');
    34→  multitrackUI.speakerList = document.getElementById('speakerList');
    35→  multitrackUI.addSpeakerBtn = document.getElementById('addSpeakerBtn');
    36→  multitrackUI.wideShotTrack = document.getElementById('wideShotTrack');
    37→
    38→  // Parameters
    39→  multitrackUI.minShotDuration = document.getElementById('minShotDuration');
    40→  multitrackUI.minShotDurationValue = document.getElementById('minShotDurationValue');
    41→  multitrackUI.wideShotPercent = document.getElementById('wideShotPercent');
    42→  multitrackUI.wideShotPercentValue = document.getElementById('wideShotPercentValue');
    43→  multitrackUI.switchingFrequency = document.getElementById('switchingFrequency');
    44→  multitrackUI.switchingFrequencyValue = document.getElementById('switchingFrequencyValue');
    45→  multitrackUI.enableWideShotDetection = document.getElementById('enableWideShotDetection');
    46→
    47→  // Actions
    48→  multitrackUI.analyzeBtn = document.getElementById('analyzeMultitrackBtn');
    49→  multitrackUI.autoBalanceBtn = document.getElementById('autoBalanceBtn');
    50→  multitrackUI.applyBtn = document.getElementById('applyMultitrackBtn');
    51→
    52→  // Preview
    53→  multitrackUI.preview = document.getElementById('multitrackPreview');
    54→  multitrackUI.distributionChart = document.getElementById('distributionChart');
    55→  multitrackUI.speakerStats = document.getElementById('speakerStats');
    56→  multitrackUI.decisionCount = document.getElementById('decisionCount');
    57→  multitrackUI.decisionList = document.getElementById('decisionList');
    58→}
    59→
    60→// ============================================================================
    61→// INITIALIZATION
    62→// ============================================================================
    63→function initMultitrackUI() {
    64→  cacheMultitrackElements();
    65→
    66→  if (!multitrackUI.section) {
    67→    console.warn('[SPLICE Multitrack] Multitrack section not found in DOM');
    68→    return;
    69→  }
    70→
    71→  // Toggle section expand/collapse
    72→  if (multitrackUI.toggle) {
    73→    multitrackUI.toggle.addEventListener('click', toggleMultitrackSection);
    74→  }
    75→
    76→  // Add speaker button
    77→  if (multitrackUI.addSpeakerBtn) {
    78→    multitrackUI.addSpeakerBtn.addEventListener('click', addSpeaker);
    79→  }
    80→
    81→  // Speaker list event delegation (remove buttons, name changes)
    82→  if (multitrackUI.speakerList) {
    83→    multitrackUI.speakerList.addEventListener('click', handleSpeakerListClick);
    84→    multitrackUI.speakerList.addEventListener('change', handleSpeakerListChange);
    85→  }
    86→
    87→  // Parameter sliders
    88→  setupParameterListeners();
    89→
    90→  // Action buttons
    91→  if (multitrackUI.analyzeBtn) {
    92→    multitrackUI.analyzeBtn.addEventListener('click', analyzeMultitrack);
    93→  }
    94→  if (multitrackUI.autoBalanceBtn) {
    95→    multitrackUI.autoBalanceBtn.addEventListener('click', autoBalanceMultitrack);
    96→  }
    97→  if (multitrackUI.applyBtn) {
    98→    multitrackUI.applyBtn.addEventListener('click', applyMultitrackCuts);
    99→  }
   100→
   101→  // Decision list event delegation (seek buttons)
   102→  if (multitrackUI.decisionList) {
   103→    multitrackUI.decisionList.addEventListener('click', handleDecisionListClick);
   104→  }
   105→
   106→  // Initialize speaker config from DOM
   107→  initSpeakerConfigFromDOM();
   108→
   109→  console.log('[SPLICE Multitrack] UI initialized');
   110→}
   111→
   112→// ============================================================================
   113→// SECTION TOGGLE
   114→// ============================================================================
   115→function toggleMultitrackSection() {
   116→  if (!multitrackUI.panel || !multitrackUI.toggle) return;
   117→
   118→  const isCollapsed = multitrackUI.panel.classList.contains('collapsed');
   119→  const icon = multitrackUI.toggle.querySelector('.toggle-icon');
   120→
   121→  if (isCollapsed) {
   122→    multitrackUI.panel.classList.remove('collapsed');
   123→    if (icon) icon.textContent = '-';
   124→  } else {
   125→    multitrackUI.panel.classList.add('collapsed');
   126→    if (icon) icon.textContent = '+';
   127→  }
   128→}
   129→
   130→// ============================================================================
   131→// SPEAKER CONFIGURATION
   132→// ============================================================================
   133→function initSpeakerConfigFromDOM() {
   134→  speakerConfig = [];
   135→  const items = multitrackUI.speakerList?.querySelectorAll('.speaker-item') || [];
   136→
   137→  items.forEach((item, index) => {
   138→    const nameInput = item.querySelector('.speaker-name');
   139→    const trackSelect = item.querySelector('.speaker-track');
   140→
   141→    speakerConfig.push({
   142→      index,
   143→      name: nameInput?.value || `Speaker ${index + 1}`,
   144→      videoTrack: parseInt(trackSelect?.value || index)
   145→    });
   146→  });
   147→}
   148→
   149→function addSpeaker() {
   150→  if (speakerConfig.length >= 4) {
   151→    setStatus('Maximum 4 speakers supported');
   152→    return;
   153→  }
   154→
   155→  const newIndex = speakerConfig.length;
   156→  const defaultName = `Speaker ${newIndex + 1}`;
   157→
   158→  speakerConfig.push({
   159→    index: newIndex,
   160→    name: defaultName,
   161→    videoTrack: newIndex
   162→  });
   163→
   164→  // Add DOM element
   165→  const speakerItem = document.createElement('div');
   166→  speakerItem.className = `speaker-item speaker-${newIndex}`;
   167→  speakerItem.dataset.speakerIndex = newIndex;
   168→  speakerItem.innerHTML = `
   169→    <input type="text" class="speaker-name" value="${defaultName}" placeholder="Name" aria-label="Speaker ${newIndex + 1} name">
   170→    <select class="speaker-track" aria-label="Speaker ${newIndex + 1} video track">
   171→      <option value="0" ${newIndex === 0 ? 'selected' : ''}>V1</option>
   172→      <option value="1" ${newIndex === 1 ? 'selected' : ''}>V2</option>
   173→      <option value="2" ${newIndex === 2 ? 'selected' : ''}>V3</option>
   174→      <option value="3" ${newIndex === 3 ? 'selected' : ''}>V4</option>
   175→    </select>
   176→    <button class="speaker-remove-btn" title="Remove speaker" aria-label="Remove speaker ${newIndex + 1}">x</button>
   177→  `;
   178→
   179→  multitrackUI.speakerList.appendChild(speakerItem);
   180→
   181→  // Update remove button visibility
   182→  updateRemoveButtonVisibility();
   183→}
   184→
   185→function removeSpeaker(index) {
   186→  if (speakerConfig.length <= 2) {
   187→    setStatus('Minimum 2 speakers required');
   188→    return;
   189→  }
   190→
   191→  speakerConfig.splice(index, 1);
   192→
   193→  // Remove from DOM
   194→  const item = multitrackUI.speakerList.querySelector(`[data-speaker-index="${index}"]`);
   195→  if (item) {
   196→    item.remove();
   197→  }
   198→
   199→  // Re-index remaining speakers
   200→  reindexSpeakers();
   201→
   202→  // Update remove button visibility
   203→  updateRemoveButtonVisibility();
   204→}
   205→
   206→function reindexSpeakers() {
   207→  const items = multitrackUI.speakerList.querySelectorAll('.speaker-item');
   208→  items.forEach((item, i) => {
   209→    item.dataset.speakerIndex = i;
   210→    item.className = `speaker-item speaker-${i}`;
   211→
   212→    // Update speakerConfig
   213→    if (speakerConfig[i]) {
   214→      speakerConfig[i].index = i;
   215→    }
   216→  });
   217→}
   218→
   219→function updateRemoveButtonVisibility() {
   220→  const items = multitrackUI.speakerList.querySelectorAll('.speaker-item');
   221→  const canRemove = items.length > 2;
   222→
   223→  items.forEach(item => {
   224→    const btn = item.querySelector('.speaker-remove-btn');
   225→    if (btn) {
   226→      btn.style.display = canRemove ? 'block' : 'none';
   227→    }
   228→  });
   229→}
   230→
   231→function handleSpeakerListClick(e) {
   232→  const removeBtn = e.target.closest('.speaker-remove-btn');
   233→  if (removeBtn) {
   234→    const item = removeBtn.closest('.speaker-item');
   235→    const index = parseInt(item?.dataset.speakerIndex || 0);
   236→    removeSpeaker(index);
   237→  }
   238→}
   239→
   240→function handleSpeakerListChange(e) {
   241→  const item = e.target.closest('.speaker-item');
   242→  if (!item) return;
   243→
   244→  const index = parseInt(item.dataset.speakerIndex || 0);
   245→
   246→  if (e.target.classList.contains('speaker-name')) {
   247→    speakerConfig[index].name = e.target.value;
   248→  } else if (e.target.classList.contains('speaker-track')) {
   249→    speakerConfig[index].videoTrack = parseInt(e.target.value);
   250→  }
   251→}
   252→
   253→// ============================================================================
   254→// PARAMETER LISTENERS
   255→// ============================================================================
   256→function setupParameterListeners() {
   257→  // Min shot duration slider
   258→  if (multitrackUI.minShotDuration && multitrackUI.minShotDurationValue) {
   259→    multitrackUI.minShotDuration.addEventListener('input', () => {
   260→      multitrackUI.minShotDurationValue.textContent =
   261→        parseFloat(multitrackUI.minShotDuration.value).toFixed(1) + 's';
   262→    });
   263→  }
   264→
   265→  // Wide shot percentage slider
   266→  if (multitrackUI.wideShotPercent && multitrackUI.wideShotPercentValue) {
   267→    multitrackUI.wideShotPercent.addEventListener('input', () => {
   268→      multitrackUI.wideShotPercentValue.textContent =
   269→        multitrackUI.wideShotPercent.value + '%';
   270→    });
   271→  }
   272→
   273→  // Switching frequency slider
   274→  if (multitrackUI.switchingFrequency && multitrackUI.switchingFrequencyValue) {
   275→    multitrackUI.switchingFrequency.addEventListener('input', () => {
   276→      multitrackUI.switchingFrequencyValue.textContent =
   277→        multitrackUI.switchingFrequency.value;
   278→    });
   279→  }
   280→}
   281→
   282→// ============================================================================
   283→// GET SETTINGS
   284→// ============================================================================
   285→function getMultitrackSettings() {
   286→  return {
   287→    minShotDuration: parseFloat(multitrackUI.minShotDuration?.value || 2),
   288→    wideShotPercentage: parseInt(multitrackUI.wideShotPercent?.value || 20),
   289→    switchingFrequency: parseInt(multitrackUI.switchingFrequency?.value || 50),
   290→    wideShotEnabled: multitrackUI.enableWideShotDetection?.checked ?? true,
   291→    wideShotTracks: multitrackUI.wideShotTrack?.value !== '-1'
   292→      ? [parseInt(multitrackUI.wideShotTrack.value)]
   293→      : [],
   294→    speakerNames: speakerConfig.map(s => s.name),
   295→    videoTrackMapping: speakerConfig.reduce((acc, s) => {
   296→      acc[s.index] = s.videoTrack;
   297→      return acc;
   298→    }, {})
   299→  };
   300→}
   301→
   302→// ============================================================================
   303→// ANALYZE MULTITRACK
   304→// ============================================================================
   305→async function analyzeMultitrack() {
   306→  if (isMultitrackOperationInProgress) return;
   307→
   308→  // Check online status
   309→  if (typeof isOnline === 'function' && !isOnline()) {
   310→    setStatus('Offline - Check your connection');
   311→    return;
   312→  }
   313→
   314→  isMultitrackOperationInProgress = true;
   315→  if (multitrackUI.analyzeBtn) {
   316→    multitrackUI.analyzeBtn.disabled = true;
   317→    multitrackUI.analyzeBtn.textContent = 'Analyzing...';
   318→  }
   319→
   320→  try {
   321→    setStatus('Exporting audio tracks...');
   322→
   323→    // Export audio for analysis
   324→    const paths = await getPaths();
   325→    if (!paths.wavPath) {
   326→      throw new Error('Audio export path not initialized');
   327→    }
   328→
   329→    // For now, we use the main audio path
   330→    // In a full implementation, we'd export separate tracks per speaker
   331→    const audioPaths = [paths.wavPath];
   332→
   333→    // Add additional tracks if multi-track sequence
   334→    // This is a placeholder - real implementation would detect sequence tracks
   335→    if (speakerConfig.length > 1) {
   336→      // Use same path for demo - in production, export each speaker's audio track
   337→      for (let i = 1; i < speakerConfig.length; i++) {
   338→        audioPaths.push(paths.wavPath);
   339→      }
   340→    }
   341→
   342→    setStatus('Analyzing speakers...');
   343→
   344→    const settings = getMultitrackSettings();
   345→    const apiUrl = getBackendUrl();
   346→
   347→    const response = await fetchWithTimeout(`${apiUrl}/multitrack`, {
   348→      method: 'POST',
   349→      headers: getAuthHeaders(),
   350→      body: JSON.stringify({
   351→        audioPaths,
   352→        speakerNames: settings.speakerNames,
   353→        videoTrackMapping: settings.videoTrackMapping,
   354→        wideShotEnabled: settings.wideShotEnabled,
   355→        wideShotPercentage: settings.wideShotPercentage,
   356→        wideShotTracks: settings.wideShotTracks,
   357→        minShotDuration: settings.minShotDuration,
   358→        switchingFrequency: settings.switchingFrequency
   359→      })
   360→    }, FETCH_TIMEOUT_PROCESSING);
   361→
   362→    if (!response.ok) {
   363→      const errorMsg = await parseErrorResponse(response);
   364→      throw new Error(errorMsg);
   365→    }
   366→
   367→    const data = await response.json();
   368→    analysisResults = data;
   369→
   370→    // Display results
   371→    displayMultitrackResults(data);
   372→
   373→    // Enable apply button
   374→    if (multitrackUI.applyBtn) {
   375→      multitrackUI.applyBtn.disabled = false;
   376→    }
   377→
   378→    setStatus(`Analysis complete - ${data.decisions?.length || 0} switching decisions`);
   379→
   380→  } catch (err) {
   381→    console.error('[SPLICE Multitrack] Analysis error:', err);
   382→    setStatus('Analysis failed: ' + err.message);
   383→  } finally {
   384→    isMultitrackOperationInProgress = false;
   385→    if (multitrackUI.analyzeBtn) {
   386→      multitrackUI.analyzeBtn.disabled = false;
   387→      multitrackUI.analyzeBtn.textContent = 'Analyze';
   388→    }
   389→  }
   390→}
   391→
   392→// ============================================================================
   393→// AUTO-BALANCE
   394→// ============================================================================
   395→async function autoBalanceMultitrack() {
   396→  if (isMultitrackOperationInProgress) return;
   397→
   398→  // Check online status
   399→  if (typeof isOnline === 'function' && !isOnline()) {
   400→    setStatus('Offline - Check your connection');
   401→    return;
   402→  }
   403→
   404→  isMultitrackOperationInProgress = true;
   405→  if (multitrackUI.autoBalanceBtn) {
   406→    multitrackUI.autoBalanceBtn.disabled = true;
   407→    multitrackUI.autoBalanceBtn.textContent = 'Balancing...';
   408→  }
   409→
   410→  try {
   411→    setStatus('Auto-balancing speaker screentime...');
   412→
   413→    const paths = await getPaths();
   414→    const audioPaths = [paths.wavPath];
   415→
   416→    // Duplicate for multiple speakers (placeholder)
   417→    for (let i = 1; i < speakerConfig.length; i++) {
   418→      audioPaths.push(paths.wavPath);
   419→    }
   420→
   421→    const settings = getMultitrackSettings();
   422→    const apiUrl = getBackendUrl();
   423→
   424→    const response = await fetchWithTimeout(`${apiUrl}/multitrack/auto-balance`, {
   425→      method: 'POST',
   426→      headers: getAuthHeaders(),
   427→      body: JSON.stringify({
   428→        audioPaths,
   429→        speakerNames: settings.speakerNames,
   430→        videoTrackMapping: settings.videoTrackMapping,
   431→        wideShotEnabled: settings.wideShotEnabled,
   432→        wideShotPercentage: settings.wideShotPercentage,
   433→        wideShotTracks: settings.wideShotTracks,
   434→        minShotDuration: settings.minShotDuration,
   435→        switchingFrequency: settings.switchingFrequency
   436→      })
   437→    }, FETCH_TIMEOUT_PROCESSING);
   438→
   439→    if (!response.ok) {
   440→      const errorMsg = await parseErrorResponse(response);
   441→      throw new Error(errorMsg);
   442→    }
   443→
   444→    const data = await response.json();
   445→    analysisResults = data;
   446→
   447→    // Display results
   448→    displayMultitrackResults(data);
   449→
   450→    // Enable apply button
   451→    if (multitrackUI.applyBtn) {
   452→      multitrackUI.applyBtn.disabled = false;
   453→    }
   454→
   455→    const errorInfo = data.finalError ? ` (${data.finalError.toFixed(1)}% deviation)` : '';
   456→    setStatus(`Auto-balance complete${errorInfo}`);
   457→
   458→  } catch (err) {
   459→    console.error('[SPLICE Multitrack] Auto-balance error:', err);
   460→    setStatus('Auto-balance failed: ' + err.message);
   461→  } finally {
   462→    isMultitrackOperationInProgress = false;
   463→    if (multitrackUI.autoBalanceBtn) {
   464→      multitrackUI.autoBalanceBtn.disabled = false;
   465→      multitrackUI.autoBalanceBtn.textContent = 'Auto-Balance';
   466→    }
   467→  }
   468→}
   469→
   470→// ============================================================================
   471→// DISPLAY RESULTS
   472→// ============================================================================
   473→function displayMultitrackResults(results) {
   474→  if (!results) return;
   475→
   476→  // Show preview section
   477→  if (multitrackUI.preview) {
   478→    multitrackUI.preview.classList.remove('hidden');
   479→  }
   480→
   481→  // Render distribution chart
   482→  renderDistributionChart(results.metadata);
   483→
   484→  // Render speaker stats
   485→  renderSpeakerStats(results.metadata);
   486→
   487→  // Render decision list
   488→  renderDecisionList(results.decisions || []);
   489→}
   490→
   491→function renderDistributionChart(metadata) {
   492→  if (!multitrackUI.distributionChart || !metadata) return;
   493→
   494→  const speakerPercentages = metadata.speakerPercentages || {};
   495→  const wideShotPct = metadata.wideShotPercentage || 0;
   496→
   497→  const fragment = document.createDocumentFragment();
   498→
   499→  // Add speaker bars
   500→  let speakerIndex = 0;
   501→  for (const [speaker, pct] of Object.entries(speakerPercentages)) {
   502→    if (pct > 0) {
   503→      const bar = document.createElement('div');
   504→      bar.className = `distribution-bar speaker-${speakerIndex % 4}`;
   505→      bar.style.width = `${pct}%`;
   506→      bar.textContent = pct >= 10 ? `${speaker}: ${pct.toFixed(0)}%` : '';
   507→      bar.title = `${speaker}: ${pct.toFixed(1)}%`;
   508→      fragment.appendChild(bar);
   509→    }
   510→    speakerIndex++;
   511→  }
   512→
   513→  // Add wide shot bar
   514→  if (wideShotPct > 0) {
   515→    const bar = document.createElement('div');
   516→    bar.className = 'distribution-bar wide-shot';
   517→    bar.style.width = `${wideShotPct}%`;
   518→    bar.textContent = wideShotPct >= 8 ? `Wide: ${wideShotPct.toFixed(0)}%` : '';
   519→    bar.title = `Wide Shot: ${wideShotPct.toFixed(1)}%`;
   520→    fragment.appendChild(bar);
   521→  }
   522→
   523→  multitrackUI.distributionChart.innerHTML = '';
   524→  multitrackUI.distributionChart.appendChild(fragment);
   525→}
   526→
   527→function renderSpeakerStats(metadata) {
   528→  if (!multitrackUI.speakerStats || !metadata) return;
   529→
   530→  const speakerPercentages = metadata.speakerPercentages || {};
   531→  const wideShotPct = metadata.wideShotPercentage || 0;
   532→
   533→  const fragment = document.createDocumentFragment();
   534→
   535→  // Add speaker stats
   536→  let speakerIndex = 0;
   537→  for (const [speaker, pct] of Object.entries(speakerPercentages)) {
   538→    const stat = document.createElement('div');
   539→    stat.className = 'speaker-stat';
   540→    stat.innerHTML = `
   541→      <span class="speaker-stat-dot speaker-${speakerIndex % 4}"></span>
   542→      <span>${speaker}: ${pct.toFixed(1)}%</span>
   543→    `;
   544→    fragment.appendChild(stat);
   545→    speakerIndex++;
   546→  }
   547→
   548→  // Add wide shot stat
   549→  if (wideShotPct > 0) {
   550→    const stat = document.createElement('div');
   551→    stat.className = 'speaker-stat';
   552→    stat.innerHTML = `
   553→      <span class="speaker-stat-dot wide-shot"></span>
   554→      <span>Wide: ${wideShotPct.toFixed(1)}%</span>
   555→    `;
   556→    fragment.appendChild(stat);
   557→  }
   558→
   559→  // Add avg shot duration
   560→  if (metadata.averageShotDuration) {
   561→    const stat = document.createElement('div');
   562→    stat.className = 'speaker-stat';
   563→    stat.innerHTML = `<span style="color: #888;">Avg: ${metadata.averageShotDuration.toFixed(1)}s</span>`;
   564→    fragment.appendChild(stat);
   565→  }
   566→
   567→  multitrackUI.speakerStats.innerHTML = '';
   568→  multitrackUI.speakerStats.appendChild(fragment);
   569→}
   570→
   571→function renderDecisionList(decisions) {
   572→  if (!multitrackUI.decisionList) return;
   573→
   574→  // Update count
   575→  if (multitrackUI.decisionCount) {
   576→    multitrackUI.decisionCount.textContent = decisions.length;
   577→  }
   578→
   579→  const fragment = document.createDocumentFragment();
   580→
   581→  // Limit to first 50 for performance
   582→  const displayDecisions = decisions.slice(0, 50);
   583→
   584→  displayDecisions.forEach((decision, i) => {
   585→    const item = document.createElement('div');
   586→    item.className = 'decision-item';
   587→
   588→    const speakerClass = decision.isWideShot ? 'wide-shot' : '';
   589→    const startTime = formatTime(decision.startTime);
   590→    const endTime = formatTime(decision.endTime);
   591→
   592→    item.innerHTML = `
   593→      <span class="decision-time">${startTime}-${endTime}</span>
   594→      <span class="decision-speaker ${speakerClass}">${decision.speakerName}</span>
   595→      <span class="decision-reason">${decision.reason || ''}</span>
   596→      <button class="decision-seek" data-time="${decision.startTime}" title="Seek to ${startTime}" aria-label="Seek to ${startTime}">&gt;</button>
   597→    `;
   598→
   599→    fragment.appendChild(item);
   600→  });
   601→
   602→  if (decisions.length > 50) {
   603→    const more = document.createElement('div');
   604→    more.className = 'decision-item';
   605→    more.style.justifyContent = 'center';
   606→    more.style.color = '#888';
   607→    more.textContent = `+ ${decisions.length - 50} more decisions`;
   608→    fragment.appendChild(more);
   609→  }
   610→
   611→  multitrackUI.decisionList.innerHTML = '';
   612→  multitrackUI.decisionList.appendChild(fragment);
   613→}
   614→
   615→function handleDecisionListClick(e) {
   616→  const seekBtn = e.target.closest('.decision-seek');
   617→  if (seekBtn) {
   618→    e.stopPropagation();
   619→    const time = parseFloat(seekBtn.dataset.time);
   620→    if (!isNaN(time) && typeof seekToTime === 'function') {
   621→      seekToTime(time);
   622→    }
   623→  }
   624→}
   625→
   626→// ============================================================================
   627→// APPLY MULTITRACK CUTS
   628→// ============================================================================
   629→async function applyMultitrackCuts() {
   630→  if (!analysisResults || !analysisResults.decisions) {
   631→    setStatus('No analysis data. Run analysis first.');
   632→    return;
   633→  }
   634→
   635→  if (isMultitrackOperationInProgress) return;
   636→
   637→  isMultitrackOperationInProgress = true;
   638→  if (multitrackUI.applyBtn) {
   639→    multitrackUI.applyBtn.disabled = true;
   640→    multitrackUI.applyBtn.textContent = 'Applying...';
   641→  }
   642→
   643→  try {
   644→    setStatus('Building multitrack sequence...');
   645→
   646→    // Check if builder is available
   647→    if (!window.spliceBuilder) {
   648→      throw new Error('Builder not initialized');
   649→    }
   650→
   651→    // Get source clip info
   652→    const ppro = require('premierepro');
   653→    const project = await ppro.Project.getActiveProject();
   654→    if (!project) throw new Error('No active project');
   655→
   656→    const rootItem = await project.getRootItem();
   657→    const sourceItem = await findFirstMediaItem(rootItem);
   658→    if (!sourceItem) throw new Error('No media items found');
   659→
   660→    // Create cut list from decisions
   661→    const cutList = {
   662→      version: '3.5',
   663→      source: {
   664→        name: sourceItem.name,
   665→        path: sourceItem.treePath
   666→      },
   667→      segments: analysisResults.decisions.map(d => ({
   668→        type: d.isWideShot ? 'wide_shot' : (d.speakerIndex >= 0 ? 'speaker' : 'speech'),
   669→        sourceName: sourceItem.name,
   670→        sourcePath: sourceItem.treePath,
   671→        inPoint: d.startTime,
   672→        outPoint: d.endTime,
   673→        speaker: d.speakerName,
   674→        videoTrack: d.videoTrack,
   675→        colorHint: d.isWideShot ? 'yellow' :
   676→                   (d.speakerIndex === 0 ? 'mango' :
   677→                    d.speakerIndex === 1 ? 'caribbean' :
   678→                    d.speakerIndex === 2 ? 'lavender' : 'cerulean')
   679→      })),
   680→      metadata: {
   681→        isMultitrack: true,
   682→        speakerCount: speakerConfig.length,
   683→        decisionCount: analysisResults.decisions.length,
   684→        ...analysisResults.metadata
   685→      }
   686→    };
   687→
   688→    // Build sequence
   689→    const result = await window.spliceBuilder.buildSequenceFromCutList(cutList, {
   690→      suffix: '_MULTITRACK'
   691→    });
   692→
   693→    if (result.success) {
   694→      // Hide preview
   695→      if (multitrackUI.preview) {
   696→        multitrackUI.preview.classList.add('hidden');
   697→      }
   698→
   699→      setStatus(`Multitrack sequence created: ${result.stats?.clipsInserted || 0} clips`);
   700→
   701→      // Clear results
   702→      analysisResults = null;
   703→    } else {
   704→      throw new Error(result.error || 'Failed to build sequence');
   705→    }
   706→
   707→  } catch (err) {
   708→    console.error('[SPLICE Multitrack] Apply error:', err);
   709→    setStatus('Apply failed: ' + err.message);
   710→  } finally {
   711→    isMultitrackOperationInProgress = false;
   712→    if (multitrackUI.applyBtn) {
   713→      multitrackUI.applyBtn.disabled = true;
   714→      multitrackUI.applyBtn.textContent = 'Apply';
   715→    }
   716→  }
   717→}
   718→
   719→// ============================================================================
   720→// HELPER: Find first media item (duplicated from main.js for module isolation)
   721→// ============================================================================
   722→async function findFirstMediaItem(item) {
   723→  if (!item) return null;
   724→
   725→  const ProjectItemType = {
   726→    CLIP: 1,
   727→    BIN: 2,
   728→    ROOT: 3,
   729→    FILE: 4
   730→  };
   731→
   732→  if (item.type === ProjectItemType.CLIP) {
   733→    return item;
   734→  }
   735→
   736→  if (item.children && item.children.numItems > 0) {
   737→    for (let i = 0; i < item.children.numItems; i++) {
   738→      const child = item.children[i];
   739→      const found = await findFirstMediaItem(child);
   740→      if (found) return found;
   741→    }
   742→  }
   743→
   744→  return null;
   745→}
   746→
   747→// ============================================================================
   748→// EXPORTS
   749→// ============================================================================
   750→// Expose for initialization from main.js
   751→if (typeof window !== 'undefined') {
   752→  window.initMultitrackUI = initMultitrackUI;
   753→  window.spliceMultitrack = {
   754→    init: initMultitrackUI,
   755→    analyze: analyzeMultitrack,
   756→    autoBalance: autoBalanceMultitrack,
   757→    apply: applyMultitrackCuts,
   758→    getSettings: getMultitrackSettings,
