   193â†’// ============================================================================
   194â†’// JSX CONNECTION CHECK
   195â†’// ============================================================================
   196â†’async function checkJSXConnection() {
   197â†’    try {
   198â†’        const result = await jsx.call('getVersion');
   199â†’        console.log('[SPLICE] JSX connection OK, version:', result);
   200â†’        return true;
   201â†’    } catch (e) {
   202â†’        console.warn('[SPLICE] JSX connection failed:', e.message);
   203â†’        setStatus('Premiere Pro connection failed', true);
   204â†’        return false;
   205â†’    }
   206â†’}
   207â†’
   208â†’async function checkSequenceOpen() {
   209â†’    try {
   210â†’        const result = await jsx.call('checkSequenceOpen');
   211â†’        return result === true || result === 'true';
   212â†’    } catch {
   213â†’        return false;
   214â†’    }
   215â†’}
