   217â†’// ============================================================================
   218â†’// OPTIONS TOGGLE
   219â†’// ============================================================================
   220â†’function initOptionsToggle() {
   221â†’    if (ui.optionsToggle && ui.optionsPanel) {
   222â†’        ui.optionsToggle.addEventListener('click', () => {
   223â†’            const isExpanded = ui.optionsPanel.classList.toggle('collapsed');
   224â†’            ui.optionsToggle.classList.toggle('expanded', !isExpanded);
   225â†’            saveSettings({ expandedOptions: !isExpanded });
   226â†’        });
   227â†’
   228â†’        // Restore state
   229â†’        const settings = getSettings();
   230â†’        if (!settings.expandedOptions) {
   231â†’            ui.optionsPanel.classList.add('collapsed');
   232â†’        } else {
   233â†’            ui.optionsToggle.classList.add('expanded');
   234â†’        }
   235â†’    }
   236â†’}
