   934â†’async function buildSequence() {
   935â†’    if (isOperationInProgress) return;
   936â†’
   937â†’    isOperationInProgress = true;
   938â†’    setStatus('Building sequence...');
   939â†’
   940â†’    try {
   941â†’        const settings = getSettings();
   942â†’
   943â†’        // Ensure we have sequence info
   944â†’        if (!currentSequenceInfo) {
   945â†’            currentSequenceInfo = await jsx.call('getActiveSequence');
   946â†’        }
   947â†’
   948â†’        // Create cut list from selected silences
   949â†’        const selectedSilences = previewSilences.filter((_, i) => selectedSilenceIndices.has(i));
   950â†’
   951â†’        // Calculate total duration from sequence info or last silence
   952â†’        let duration = 0;
   953â†’        if (currentSequenceInfo && currentSequenceInfo.duration) {
   954â†’            duration = currentSequenceInfo.duration;
   955â†’        } else if (previewSilences.length > 0) {
   956â†’            const lastSilence = previewSilences[previewSilences.length - 1];
   957â†’            duration = lastSilence.end + 10; // Add buffer
   958â†’        }
   959â†’
   960â†’        // Call backend to generate cut list
   961â†’        const cutListResponse = await fetchWithTimeout(
   962â†’            `${getBackendUrl()}/cut-list`,
   963â†’            {
   964â†’                method: 'POST',
   965â†’                headers: getAuthHeaders(),
   966â†’                body: JSON.stringify({
   967â†’                    sourceName: currentSequenceInfo?.name || 'Untitled Sequence',
   968â†’                    sourcePath: currentSequenceInfo?.treePath || currentSequenceInfo?.name || 'Untitled',
   969â†’                    duration: duration,
   970â†’                    silences: selectedSilences,
   971â†’                    takes: previewTakes,
   972â†’                    enableTakes: settings.enableTakesDetection,
   973â†’                    jCutOffset: settings.enableJCut ? settings.jcutLeadIn : 0,
   974â†’                    lCutOffset: settings.enableJCut ? settings.jcutLeadOut : 0
   975â†’                })
   976â†’            }
   977â†’        );
   978â†’
   979â†’        if (!cutListResponse.ok) {
   980â†’            throw new Error(await parseErrorResponse(cutListResponse));
   981â†’        }
   982â†’
   983â†’        const cutList = await cutListResponse.json();
   984â†’
   985â†’        // Build sequence via JSX
   986â†’        const result = await jsx.call('buildSequenceFromCutList', JSON.stringify(cutList));
   987â†’
   988â†’        if (result && result.success) {
   989â†’            setStatus(`Built sequence: ${result.sequenceName}`);
   990â†’            hidePreview();
   991â†’        } else {
   992â†’            throw new Error(result?.error || 'Build failed');
   993â†’        }
   994â†’    } catch (error) {
   995â†’        setStatus('Build failed: ' + error.message, true);
   996â†’    } finally {
   997â†’        isOperationInProgress = false;
   998â†’    }
   999â†’}
