   486â†’function setupTabSwitching() {
   487â†’  const tabs = document.querySelectorAll('.music-tab');
   488â†’  const contents = {
   489â†’    generate: musicElements.tabGenerate || document.getElementById('music-tab-generate'),
   490â†’    identify: musicElements.tabIdentify || document.getElementById('music-tab-identify'),
   491â†’    library: musicElements.tabLibrary || document.getElementById('music-tab-library')
   492â†’  };
   493â†’
   494â†’  tabs.forEach(tab => {
   495â†’    tab.addEventListener('click', () => {
   496â†’      const tabName = tab.dataset.tab;
   497â†’
   498â†’      // Update tab buttons
   499â†’      tabs.forEach(t => t.classList.remove('active'));
   500â†’      tab.classList.add('active');
   501â†’
   502â†’      // Update tab content
   503â†’      Object.entries(contents).forEach(([name, content]) => {
   504â†’        if (content) {
   505â†’          content.classList.toggle('active', name === tabName);
   506â†’        }
   507â†’      });
   508â†’
   509â†’      // Refresh library when switching to library tab
   510â†’      if (tabName === 'library') {
   511â†’        loadMusicLibrary();
   512â†’      }
   513â†’    });
   514â†’  });
   515â†’}
