   267â†’/**
   268â†’ * Handle text changes in editor
   269â†’ */
   270â†’function handleTextChange(newText) {
   271â†’  // Save current state to undo stack
   272â†’  if (textEditorState.currentText !== newText) {
   273â†’    textEditorState.undoStack.push(textEditorState.currentText);
   274â†’    textEditorState.redoStack = []; // Clear redo stack on new edit
   275â†’
   276â†’    // Limit undo stack size
   277â†’    if (textEditorState.undoStack.length > MAX_UNDO_STACK) {
   278â†’      textEditorState.undoStack.shift();
   279â†’    }
   280â†’  }
   281â†’
   282â†’  textEditorState.currentText = newText;
   283â†’  textEditorState.isDirty = true;
   284â†’  textEditorState.previewChanges = null; // Clear preview
   285â†’
   286â†’  updateEditorStatus();
   287â†’}
   288â†’
   289â†’/**
   290â†’ * Undo last text edit
   291â†’ */
   292â†’function undoTextEdit() {
   293â†’  if (textEditorState.undoStack.length === 0) {
   294â†’    showTextEditorStatus('Nothing to undo', 'info');
   295â†’    return;
   296â†’  }
   297â†’
   298â†’  // Save current to redo stack
   299â†’  textEditorState.redoStack.push(textEditorState.currentText);
   300â†’
   301â†’  // Restore from undo stack
   302â†’  textEditorState.currentText = textEditorState.undoStack.pop();
   303â†’
   304â†’  // Update editor content
   305â†’  const editor = textEditorElements.content || document.getElementById('text-editor-content');
   306â†’  if (editor) {
   307â†’    editor.value = textEditorState.currentText;
   308â†’  }
   309â†’
   310â†’  updateEditorStatus();
   311â†’  showTextEditorStatus('Undo applied', 'info');
   312â†’}
   313â†’
   314â†’/**
   315â†’ * Redo last undone edit
   316â†’ */
   317â†’function redoTextEdit() {
