   330â†’  const editor = textEditorElements.content || document.getElementById('text-editor-content');
   331â†’  if (editor) {
   332â†’    editor.value = textEditorState.currentText;
   333â†’  }
   334â†’
   335â†’  updateEditorStatus();
   336â†’  showTextEditorStatus('Redo applied', 'info');
   337â†’}
