  1051â†’function buildSequenceFromCutList(cutListJson) {
  1052â†’    try {
  1053â†’        var cutList = JSON.parse(cutListJson);
  1054â†’        var seq = app.project.activeSequence;
  1055â†’        if (!seq) return JSON.stringify({ error: "No active sequence" });
  1056â†’
  1057â†’        // Clone the sequence
  1058â†’        var newSequenceName = seq.name + "_SPLICE";
  1059â†’        seq.clone();
  1060â†’
  1061â†’        // Find the cloned sequence
  1062â†’        var newSeq = null;
  1063â†’        for (var i = app.project.sequences.numSequences - 1; i >= 0; i--) {
  1064â†’            var s = app.project.sequences[i];
  1065â†’            if (s.name.indexOf(seq.name) === 0 && s.sequenceID !== seq.sequenceID) {
  1066â†’                newSeq = s;
  1067â†’                break;
  1068â†’            }
  1069â†’        }
  1070â†’
  1071â†’        if (!newSeq) {
  1072â†’            return JSON.stringify({ error: "Failed to clone sequence" });
  1073â†’        }
  1074â†’
  1075â†’        newSeq.name = newSequenceName;
  1076â†’        app.project.activeSequence = newSeq;
  1077â†’
  1078â†’        // Clear all clips from the new sequence
  1079â†’        clearSequence();
  1080â†’
  1081â†’        // Insert segments
  1082â†’        var currentPosition = 0;
  1083â†’        var stats = {
  1084â†’            segmentsInserted: 0,
  1085â†’            totalDuration: 0
  1086â†’        };
  1087â†’
  1088â†’        for (var i = 0; i < cutList.segments.length; i++) {
  1089â†’            var segment = cutList.segments[i];
  1090â†’
  1091â†’            // Find the source project item
  1092â†’            var sourceItem = null;
  1093â†’            if (segment.sourcePath) {
  1094â†’                sourceItem = findItemByPath(app.project.rootItem, segment.sourcePath);
  1095â†’            }
  1096â†’            if (!sourceItem && segment.sourceName) {
  1097â†’                sourceItem = findItemByName(app.project.rootItem, segment.sourceName);
  1098â†’            }
  1099â†’
  1100â†’            if (!sourceItem) continue;
  1101â†’
  1102â†’            // Set color label
  1103â†’            if (segment.colorHint && SEGMENT_COLORS[segment.colorHint]) {
  1104â†’                sourceItem.setColorLabel(SEGMENT_COLORS[segment.colorHint]);
  1105â†’            } else if (segment.type && SEGMENT_COLORS[segment.type]) {
  1106â†’                sourceItem.setColorLabel(SEGMENT_COLORS[segment.type]);
  1107â†’            }
  1108â†’
  1109â†’            // Insert clip at current position
  1110â†’            var targetTime = new Time();
  1111â†’            targetTime.seconds = currentPosition;
  1112â†’
  1113â†’            newSeq.videoTracks[0].insertClip(sourceItem, targetTime);
  1114â†’
  1115â†’            // Set in/out points
  1116â†’            var clipDuration = segment.outPoint - segment.inPoint;
  1117â†’            var videoClips = newSeq.videoTracks[0].clips;
  1118â†’            var insertedClip = videoClips[videoClips.numItems - 1];
  1119â†’
  1120â†’            if (insertedClip) {
  1121â†’                var inTime = new Time();
  1122â†’                inTime.seconds = segment.inPoint;
  1123â†’                insertedClip.inPoint = inTime;
  1124â†’
  1125â†’                var outTime = new Time();
  1126â†’                outTime.seconds = segment.outPoint;
  1127â†’                insertedClip.outPoint = outTime;
  1128â†’
  1129â†’                // Set clip name if takeLabel provided
  1130â†’                if (segment.takeLabel) {
  1131â†’                    insertedClip.name = segment.takeLabel;
  1132â†’                }
  1133â†’            }
  1134â†’
  1135â†’            currentPosition += clipDuration;
  1136â†’            stats.segmentsInserted++;
  1137â†’            stats.totalDuration += clipDuration;
  1138â†’        }
  1139â†’
  1140â†’        return JSON.stringify({
  1141â†’            success: true,
  1142â†’            sequenceName: newSequenceName,
  1143â†’            stats: stats
  1144â†’        });
  1145â†’    } catch (e) {
  1146â†’        return JSON.stringify({ error: e.message });
  1147â†’    }
  1148â†’}
