     6â†’// ============================================================================
     7â†’// BACKEND CONFIGURATION
     8â†’// ============================================================================
     9â†’const SPLICE_CONFIG = {
    10â†’    VERSION: '6.0.2',
    11â†’    BACKEND_PROD: 'https://splice-api-production.up.railway.app',
    12â†’    BACKEND_DEV: 'https://127.0.0.1:3847',
    13â†’    FETCH_TIMEOUT: 120000, // 2 minutes
    14â†’    RETRY_ATTEMPTS: 3,
    15â†’    RETRY_DELAY: 1000
    16â†’};
    17â†’
    18â†’/**
    19â†’ * Get the backend URL based on environment
    20â†’ */
    21â†’function getBackendUrl() {
    22â†’    // Check for development mode
    23â†’    const isDev = window.location.protocol === 'file:' ||
    24â†’                  window.location.hostname === 'localhost' ||
    25â†’                  window.location.hostname === '127.0.0.1';
    26â†’
    27â†’    // Allow override via localStorage
    28â†’    const customUrl = localStorage.getItem('spliceBackendUrl');
    29â†’    if (customUrl) return customUrl;
    30â†’
    31â†’    return isDev ? SPLICE_CONFIG.BACKEND_DEV : SPLICE_CONFIG.BACKEND_PROD;
    32â†’}
    33â†’
    34â†’// ============================================================================
    35â†’// JSX BRIDGE - Communicate with Premiere Pro
    36â†’// ============================================================================
    37â†’const jsx = {
    38â†’    cs: null,
    39â†’    _initPromise: null,
    40â†’    _initialized: false,
    41â†’
    42â†’    /**
    43â†’     * Initialize the CSInterface
    44â†’     * Returns a promise that resolves when ready
    45â†’     */
    46â†’    init: function() {
    47â†’        if (this._initPromise) return this._initPromise;
    48â†’
    49â†’        this._initPromise = new Promise((resolve) => {
    50â†’            if (typeof CSInterface !== 'undefined') {
    51â†’                this.cs = new CSInterface();
    52â†’                this._initialized = true;
    53â†’                console.log('[JSX] CSInterface initialized successfully');
    54â†’            } else {
    55â†’                console.warn('[JSX] CSInterface not available');
    56â†’            }
    57â†’            resolve(this._initialized);
    58â†’        });
    59â†’
    60â†’        return this._initPromise;
    61â†’    },
    62â†’
    63â†’    /**
    64â†’     * Ensure JSX is initialized before making calls
    65â†’     */
    66â†’    ensureInitialized: async function() {
    67â†’        if (!this._initialized) {
    68â†’            await this.init();
    69â†’        }
    70â†’        if (!this.cs) {
    71â†’            throw new Error('CSInterface not initialized. Make sure you are running in CEP environment.');
    72â†’        }
    73â†’    },
    74â†’
    75â†’    /**
    76â†’     * Execute ExtendScript in Premiere Pro
    77â†’     * @param {string} script - ExtendScript to execute
    78â†’     * @returns {Promise<any>} - Parsed result
    79â†’     */
    80â†’    evalScript: async function(script) {
    81â†’        await this.ensureInitialized();
    82â†’
    83â†’        return new Promise((resolve, reject) => {
    84â†’            this.cs.evalScript(script, (result) => {
    85â†’                if (result === 'undefined' || result === undefined) {
    86â†’                    resolve(null);
    87â†’                    return;
    88â†’                }
    89â†’
    90â†’                // Check for EvalScript error
    91â†’                if (typeof result === 'string' && result.indexOf('EvalScript error') !== -1) {
    92â†’                    reject(new Error(result));
    93â†’                    return;
    94â†’                }
    95â†’
    96â†’                // Try to parse JSON
    97â†’                try {
    98â†’                    const parsed = JSON.parse(result);
    99â†’                    if (parsed.error) {
   100â†’                        reject(new Error(parsed.error));
   101â†’                    } else {
   102â†’                        resolve(parsed);
   103â†’                    }
   104â†’                } catch (e) {
   105â†’                    // Return as-is if not JSON
   106â†’                    resolve(result);
   107â†’                }
   108â†’            });
   109â†’        });
   110â†’    },
   111â†’
   112â†’    /**
   113â†’     * Call a JSX function with arguments
   114â†’     * @param {string} funcName - Function name
   115â†’     * @param {...any} args - Arguments to pass
   116â†’     * @returns {Promise<any>}
   117â†’     */
   118â†’    call: async function(funcName, ...args) {
   119â†’        await this.ensureInitialized();
   120â†’
   121â†’        const escapedArgs = args.map(arg => {
   122â†’            if (arg === null || arg === undefined) return 'null';
   123â†’            if (typeof arg === 'string') return `'${arg.replace(/'/g, "\\'").replace(/\n/g, '\\n')}'`;
   124â†’            if (typeof arg === 'object') return `'${JSON.stringify(arg).replace(/'/g, "\\'")}'`;
   125â†’            return String(arg);
   126â†’        });
   127â†’
   128â†’        const script = `${funcName}(${escapedArgs.join(', ')})`;
   129â†’        return this.evalScript(script);
   130â†’    }
   131â†’};
   132â†’
   133â†’// ============================================================================
   134â†’// FETCH UTILITIES
   135â†’// ============================================================================
   136â†’
   137â†’/**
   138â†’ * Fetch with timeout support
   139â†’ * @param {string} url - URL to fetch
   140â†’ * @param {object} options - Fetch options
   141â†’ * @param {number} timeout - Timeout in ms
   142â†’ */
   143â†’async function fetchWithTimeout(url, options = {}, timeout = SPLICE_CONFIG.FETCH_TIMEOUT) {
   144â†’    const controller = new AbortController();
   145â†’    const timeoutId = setTimeout(() => controller.abort(), timeout);
   146â†’
   147â†’    try {
   148â†’        const response = await fetch(url, {
   149â†’            ...options,
   150â†’            signal: controller.signal
   151â†’        });
   152â†’        clearTimeout(timeoutId);
   153â†’        return response;
   154â†’    } catch (error) {
   155â†’        clearTimeout(timeoutId);
   156â†’        if (error.name === 'AbortError') {
   157â†’            throw new Error(`Request timeout after ${timeout}ms`);
   158â†’        }
   159â†’        throw error;
   160â†’    }
   161â†’}
   162â†’
   163â†’/**
   164â†’ * Parse error response from backend
   165â†’ * @param {Response} response - Fetch response
   166â†’ */
   167â†’async function parseErrorResponse(response) {
   168â†’    try {
   169â†’        const data = await response.json();
   170â†’        return data.error || data.message || `HTTP ${response.status}`;
   171â†’    } catch {
   172â†’        return `HTTP ${response.status}: ${response.statusText}`;
   173â†’    }
   174â†’}
   175â†’
   176â†’/**
   177â†’ * Get auth headers for API requests
   178â†’ */
   179â†’function getAuthHeaders() {
   180â†’    const headers = { 'Content-Type': 'application/json' };
   181â†’    const settings = getSettings();
   182â†’
   183â†’    if (settings.customerId) {
   184â†’        headers['x-stripe-customer-id'] = settings.customerId;
   185â†’    }
   186â†’    if (settings.licenseKey) {
   187â†’        headers['x-license-key'] = settings.licenseKey;
   188â†’    }
   189â†’
   190â†’    return headers;
   191â†’}
