   205â†’    enableZoom: false,
   206â†’    zoomFrequency: 'medium',
   207â†’    zoomPreset: 'medium',
   208â†’    zoomPlacement: 'sentence_start',
   209â†’    enableChapters: false,
   210â†’    maxChapters: 10,
