   260â†’// ============================================================================
   261â†’// OFFLINE DETECTION
   262â†’// ============================================================================
   263â†’
   264â†’let isOnlineState = true;
   265â†’let offlineCheckInterval = null;
   266â†’
   267â†’/**
   268â†’ * Check if online
   269â†’ */
   270â†’function isOnline() {
