   332â†’/**
   333â†’ * Format time in MM:SS or HH:MM:SS
   334â†’ */
   335â†’function formatTime(seconds) {
   336â†’    if (isNaN(seconds) || seconds < 0) return '0:00';
   337â†’
   338â†’    const hours = Math.floor(seconds / 3600);
   339â†’    const minutes = Math.floor((seconds % 3600) / 60);
   340â†’    const secs = Math.floor(seconds % 60);
   341â†’
   342â†’    if (hours > 0) {
   343â†’        return `${hours}:${minutes.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
   344â†’    }
   345â†’    return `${minutes}:${secs.toString().padStart(2, '0')}`;
   346â†’}
