   553â†’/**
   554â†’ * Initialize preset selector
   555â†’ */
   556â†’function initPresetSelector() {
   557â†’    const presetSelector = document.getElementById('presetSelector');
   558â†’    const sensitivitySlider = document.getElementById('sensitivitySlider');
   559â†’
   560â†’    if (!presetSelector) return;
   561â†’
   562â†’    // Set initial value from settings
   563â†’    const settings = getSettings();
   564â†’    presetSelector.value = settings.activePreset || 'podcast';
   565â†’
   566â†’    // Handle preset change
   567â†’    presetSelector.addEventListener('change', () => {
   568â†’        const presetId = presetSelector.value;
   569â†’        const appliedSettings = applyPreset(presetId);
   570â†’
   571â†’        // Update sensitivity slider to match preset
   572â†’        if (sensitivitySlider && appliedSettings.sensitivity !== undefined) {
   573â†’            sensitivitySlider.value = appliedSettings.sensitivity;
   574â†’            // Update display value
   575â†’            const sensitivityValue = document.getElementById('sensitivityValue');
   576â†’            if (sensitivityValue) {
   577â†’                sensitivityValue.textContent = appliedSettings.sensitivity;
   578â†’            }
   579â†’        }
   580â†’
   581â†’        // Update takes detection checkbox
   582â†’        const enableTakesDetection = document.getElementById('enableTakesDetection');
   583â†’        if (enableTakesDetection && appliedSettings.enableTakesDetection !== undefined) {
   584â†’            enableTakesDetection.checked = appliedSettings.enableTakesDetection;
   585â†’        }
   586â†’
