// Music credits per tier
// Matches new pricing: Starter $15/mo, Pro $39/mo, Team $129/mo
const TIER_MUSIC_CREDITS = {
  starter: 2,    // 2 songs/month
  pro: 10,       // 10 songs/month
  team: 50,      // 50 songs/month
  cancelled: 0
};

// Feature access by tier - defines which features each tier can use
const TIER_FEATURES = {
  starter: ['silence_removal', 'take_detection', 'basic_captions', 'filler_word', 'caption_export'],
