    addChapterMarkers,
    addZoomMarkers,
    clearSpliceMarkers,

    // Razor
    razorAtSilences,
    deleteClipsAtSilences,

    // Utilities
    getColorForSegment,
    getActiveSequence,
    checkSequenceOpen,
    cloneSequence,
    undo
};

