   940→async function buildSequence() {
   941→    if (isOperationInProgress) return;
   942→
   943→    isOperationInProgress = true;
   944→    setStatus('Building sequence...');
   945→
   946→    try {
   947→        const settings = getSettings();
   948→
   949→        // Ensure we have sequence info
   950→        if (!currentSequenceInfo) {
   951→            currentSequenceInfo = await jsx.call('getActiveSequence');
   952→        }
   953→
   954→        // Create cut list from selected silences
   955→        const selectedSilences = previewSilences.filter((_, i) => selectedSilenceIndices.has(i));
   956→
   957→        // Calculate total duration from sequence info or last silence
   958→        let duration = 0;
   959→        if (currentSequenceInfo && currentSequenceInfo.duration) {
   960→            duration = currentSequenceInfo.duration;
   961→        } else if (previewSilences.length > 0) {
   962→            const lastSilence = previewSilences[previewSilences.length - 1];
   963→            duration = lastSilence.end + 10; // Add buffer
   964→        }
   965→
   966→        // Call backend to generate cut list
   967→        const cutListResponse = await fetchWithTimeout(
   968→            `${getBackendUrl()}/cut-list`,
   969→            {
   970→                method: 'POST',
   971→                headers: getAuthHeaders(),
   972→                body: JSON.stringify({
   973→                    sourceName: currentSequenceInfo?.name || 'Untitled Sequence',
   974→                    sourcePath: currentSequenceInfo?.treePath || currentSequenceInfo?.name || 'Untitled',
   975→                    duration: duration,
   976→                    silences: selectedSilences,
   977→                    takes: previewTakes,
   978→                    enableTakes: settings.enableTakesDetection,
   979→                    jCutOffset: settings.enableJCut ? settings.jcutLeadIn : 0,
   980→                    lCutOffset: settings.enableJCut ? settings.jcutLeadOut : 0
   981→                })
   982→            }
   983→        );
   984→
   985→        if (!cutListResponse.ok) {
   986→            throw new Error(await parseErrorResponse(cutListResponse));
   987→        }
   988→
   989→        const cutList = await cutListResponse.json();
   990→
   991→        // Build sequence via JSX
   992→        const result = await jsx.call('buildSequenceFromCutList', JSON.stringify(cutList));
   993→
   994→        if (result && result.success) {
   995→            setStatus(`Built sequence: ${result.sequenceName}`);
   996→            hidePreview();
   997→        } else {
   998→            throw new Error(result?.error || 'Build failed');
   999→        }
  1000→    } catch (error) {
  1001→        setStatus('Build failed: ' + error.message, true);
  1002→    } finally {
  1003→        isOperationInProgress = false;
  1004→    }
  1005→}
